INSERT INTO dataset_versions (version, manifest, schema, fallback_version) VALUES ('2025-09-27', '{"version":"2025-09-27","schema":"schema.json","nodes":"nodes.json","playlists":"playlists.json","sources":"sources.json","assertions":"assertions.json","entities":"entities.json"}', '{"version":"2025-09-27","createdAt":"2025-09-27T00:00:00.000Z","taxonomies":{"identity":["individual","swf","public_pension","corporate_pension","endowment","foundation","insurance","central_bank","government"],"audience":["family_friends","fiduciary","stakeholders","colleagues","regulated","individuals","staff","consultants","boards"],"level":["plain","technical"],"motivation":["regulatory_drivers","internal_leadership","external_stakeholders"]}}', NULL) ON CONFLICT(version) DO UPDATE SET manifest=excluded.manifest, schema=excluded.schema, fallback_version=excluded.fallback_version;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_apartheid-divestment-was-chaos-why-repeat-it', '{"id":"ctr_apartheid-divestment-was-chaos-why-repeat-it","type":"counter","claim":"Apartheid divestment was chaos; why repeat it?","response":"Apartheid divestment proved you can stand on ethics without breaking returns. Our firm’s policy embraces that precedent: we run apartheid-free portfolios with documented process and rely on sustained, multi-channel pressure—divestment plus engagement—just as history shows.","citations":["src_what-i-d-tell-your-cio-about-divestment","src_ethical-capital-investment-screening-policy"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_bds-is-illegal-in-our-state', '{"id":"ctr_bds-is-illegal-in-our-state","type":"counter","claim":"BDS is illegal in our state.","response":"State procurement laws target country boycotts. A conduct-based screen—grounded in UN Guiding Principles and applied to any violator—has survived review and mirrors how public systems like UC Regents structure exclusions.","citations":["src_un-guiding-principles-on-business-human-rights","src_koontz-v-watson-opinion","src_amawi-v-pflugerville-isd-opinion","src_jordahl-v-brnovich-opinion"],"targets":{"audience":["boards","staff","consultants","regulated","family_friends"],"level":["plain","technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_corporate_divestment_violates_fiduciary', '{"id":"ctr_corporate_divestment_violates_fiduciary","type":"counter","claim":"Divesting violates our ERISA fiduciary duty.","response":"ERISA requires a prudent pecuniary case, not silence. DOL guidance confirms fiduciaries may weigh conduct risks when they affect returns, and our policy keeps financial guardrails such as tracking-error caps, override clauses, and independent consultant opinions that show we are mitigating risk, not trading returns for politics.","citations":["src_us-dol-esg-guidance","src_goodwinlaw_dol_abandons_esg_rule","src_berkeley_irle_divestment_fiduciary"],"targets":{"audience":["boards","consultants"],"level":["plain"],"identity":["corporate_pension"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_corporate_engage_not_divest', '{"id":"ctr_corporate_engage_not_divest","type":"counter","claim":"We should keep the holdings and just engage.","response":"Consultants note engagement has limits when risks are tied to illegal conduct or unresponsive governance. Cambridge Associates and Callan both advise pairing escalation with divestment when conduct gaps persist, and our staged policy documents that the risk is material and that we exhausted less disruptive tools first.","citations":["src_cambridge_divestment_governance","src_callan_divest_or_engage"],"targets":{"audience":["boards","consultants","staff"],"level":["plain"],"identity":["corporate_pension"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_corporate_policy_is_discriminatory', '{"id":"ctr_corporate_policy_is_discriminatory","type":"counter","claim":"This policy exposes us to discrimination claims.","response":"Advisory Opinion 2025-04A shows the answer is to tighten the language, not abandon the policy. We rely on neutral conduct-based criteria validated by UN data and maintain counsel-reviewed override logs so the screen targets documented violations everywhere, not any protected class or geography.","citations":["src_dol_advisory_opinion_2025_04a","src_jdsupra_dol_pulls_racial_equity_guidance"],"targets":{"audience":["boards","consultants"],"level":["plain"],"identity":["corporate_pension"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_divestment-is-ineffective-someone-else-just-buys-the-shares', '{"id":"ctr_divestment-is-ineffective-someone-else-just-buys-the-shares","type":"counter","claim":"Divestment is ineffective; someone else just buys the shares.","response":"The primary effects are reputational and in primary capital markets. Policies influence lenders, underwriters, and index providers and shift the cost of capital for targeted issuers.","citations":["src_oxford-ssee-research"],"targets":{"audience":["boards","staff","stakeholders","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_divestment-will-significantly-hurt-performance-and-beneficiaries', '{"id":"ctr_divestment-will-significantly-hurt-performance-and-beneficiaries","type":"counter","claim":"Divestment will significantly hurt performance and beneficiaries.","response":"Naive exclusions can be costly, but professional managers use optimization and tracking-error limits to preserve factor exposures and benchmark-like returns.","citations":["src_msci-esg-index-construction"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_esg-has-underperformed-we-re-moving-away-from-values-investing', '{"id":"ctr_esg-has-underperformed-we-re-moving-away-from-values-investing","type":"counter","claim":"ESG has underperformed/We''re moving away from values investing.","response":"Our approach is high-active-share, research-driven investing—not an index fad. Ethical Capital''s growth strategy has delivered benchmark-like returns while holding higher cash-flow yield companies, showing exclusion discipline can coexist with market performance.","citations":["src_what-i-d-tell-your-cio-about-divestment","src_nyu-stern-esg-and-financial-performance","src_mdpi-esg-scores-and-stock-returns"],"targets":{"audience":["boards","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_exclusions-reduce-diversification-too-much', '{"id":"ctr_exclusions-reduce-diversification-too-much","type":"counter","claim":"Exclusions reduce diversification too much.","response":"Well-designed policies are narrow and conduct-based. Managers maintain diversification across sectors and factors while removing a limited set of issuers that breach defined standards.","citations":["src_msci-portfolio-construction"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_exclusions-violate-fiduciary-duty', '{"id":"ctr_exclusions-violate-fiduciary-duty","type":"counter","claim":"Exclusions violate fiduciary duty.","response":"Fiduciary duty requires prudence, loyalty, and attention to material risk and client objectives. Values alignment and risk management can be implemented within a disciplined, return-seeking mandate.","citations":["src_un-pri-fiduciary-duty","src_us-dol-esg-guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_foundation_divestment_hurts_returns', '{"id":"ctr_foundation_divestment_hurts_returns","type":"counter","claim":"Divesting or adding MRIs violates our duty to maximize returns.","response":"UPMIFA and IRS guidance both recognize that mission can be part of prudence. Boards that document risk, return, and mission factors in a prudence memo satisfy ordinary business care while reducing reputational and regulatory risk.","citations":["src_upmifa_text","src_irs_jeopardizing_investments","src_irs_notice_2015_62"],"targets":{"identity":["foundation","endowment"],"audience":["boards","stakeholders"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_foundation_spending_erosion', '{"id":"ctr_foundation_spending_erosion","type":"counter","claim":"If we fund MRIs or PRIs, we will erode the corpus and jeopardize grants.","response":"PRIs count toward the 5% payout and recycle capital as repayments arrive; MRIs live in the endowment and can grow the base for future distributions. The IRS payout rules stay intact as long as the foundation keeps the 5% floor.","citations":["src_irs_private_foundation_payout"],"targets":{"identity":["foundation","endowment"],"audience":["boards","stakeholders"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_how-do-we-define-settlement-involvement', '{"id":"ctr_how-do-we-define-settlement-involvement","type":"counter","claim":"How do we define ''settlement involvement''?","response":"Use objective sources: UN databases, AFSC Investigate, and vendor ESG datasets itemize settlement-linked projects. Policies trigger off this evidence and require periodic review.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_klp-divests-settlement-companies"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_how-do-you-know-these-activist-lists-aren-t-biased', '{"id":"ctr_how-do-you-know-these-activist-lists-aren-t-biased","type":"counter","claim":"How do you know these activist lists aren''t biased?","response":"We do not outsource ethics to third-party lists. About 40% of our exclusions are unique because we rebuild evidence in-house and document every call; activist data is an input, not the final answer.","citations":["src_ethical-capital-investment-screening-policy","src_ssrn-esg-ratings-divergence"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_insurance_divestment_weakens_surplus', '{"id":"ctr_insurance_divestment_weakens_surplus","type":"counter","claim":"Selling blocks or risky assets will weaken surplus and hurt growth.","response":"RBC math runs the other direction: shedding high-charge assets lifts statutory surplus and frees capital for higher-ROE lines. We are redeploying that surplus into regulated, admitted assets that keep growth on track.","citations":["src_debevoise_naic_rbc_task_force","src_conning_insurance_saa_2017"],"targets":{"identity":["insurance"],"audience":["boards","stakeholders"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_insurance_rating_agency_penalty', '{"id":"ctr_insurance_rating_agency_penalty","type":"counter","claim":"Rating agencies will punish us for shrinking the balance sheet.","response":"Analysts reward capital adequacy and risk profile improvements. We are delivering pro-forma models that show higher redundancy at target ratings and engaging Moody''s, S&P, Fitch, and AM Best before the announcement.","citations":["src_deloitte_insurer_divestment_2021","src_artemis_sidecar_surge_2025"],"targets":{"identity":["insurance"],"audience":["boards","consultants","stakeholders"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_israeli-companies-provide-essential-technology-services', '{"id":"ctr_israeli-companies-provide-essential-technology-services","type":"counter","claim":"Israeli companies provide essential technology/services.","response":"A conduct-first policy distinguishes between legitimate business and grave rights violations. Exclusions target companies whose revenue or technology is tied to occupation abuses; suppliers meeting standards remain investable.","citations":["src_klp-divests-settlement-companies","src_akademikerpension-statement","src_afsc-investigate"],"targets":{"audience":["boards","staff","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_msci-or-sustainability-ratings-are-more-credible-than-activist-data', '{"id":"ctr_msci-or-sustainability-ratings-are-more-credible-than-activist-data","type":"counter","claim":"MSCI or Sustainability ratings are more credible than activist data.","response":"We build proprietary screens because mainstream ratings are inconsistent and often influenced by commercial incentives. Specialist intelligence (AFSC, UN) identifies issues faster, and we overlay that insight with widely-used index tools for transparent delivery.","citations":["src_afsc-investigate","src_ssrn-esg-ratings-divergence","src_ssrn-trustworthy-esg-ratings","src_ethical-capital-investment-screening-policy"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_our-endowment-foundation-documents-prohibit-political-positions', '{"id":"ctr_our-endowment-foundation-documents-prohibit-political-positions","type":"counter","claim":"Our endowment/foundation documents prohibit political positions.","response":"Conduct-based policies focus on fiduciary risk, not partisan statements. UC Regents'' guidelines and UTS''s SRI history show boards can codify exclusions as part of their duty to manage reputational and legal risk.","citations":["src_uc-regents-investment-policy","src_union-theological-seminary-divestment"],"targets":{"audience":["boards","staff"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_our-index-funds-can-t-accommodate-exclusions', '{"id":"ctr_our-index-funds-can-t-accommodate-exclusions","type":"counter","claim":"Our index funds can''t accommodate exclusions.","response":"Major providers offer screened versions of core indexes, and mandates can overlay exclusion lists via optimization. Public pensions already run Israel/settlement screens inside benchmark-aware vehicles.","citations":["src_ftse-russell-squaring-the-circle-on-carbon-track","src_msci-esg-indexes-factsheet","src_church-of-england-pensions-board-divestment"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_our-investment-consultant-manager-won-t-implement-this', '{"id":"ctr_our-investment-consultant-manager-won-t-implement-this","type":"counter","claim":"Our investment consultant/manager won''t implement this.","response":"Ask for transparency about current constraints. Managers already deliver exclusion mandates; mainstream ESG providers offer screened benchmarks, and active oversight keeps implementation honest even when broad-market funds remain.","citations":["src_msci-esg-indexes-factsheet","src_ftse-russell-squaring-the-circle-on-carbon-track","src_church-of-england-pensions-board-divestment","src_ssrn-esg-ratings-divergence"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_public_divestment_illegal', '{"id":"ctr_public_divestment_illegal","type":"counter","claim":"Divesting for conduct reasons is illegal under anti-ESG laws.","response":"Most statutes—including Texas SB13—allow actions tied to financially material risk or legal compliance. Document risk analysis, maintain boycott compliance logs, and show the decision protects beneficiaries, not politics.","citations":["src_texas_sb13","src_texas_comptroller_energy_boycott_list"],"targets":{"identity":["public_pension"],"audience":["boards","stakeholders"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_public_process_burdensome', '{"id":"ctr_public_process_burdensome","type":"counter","claim":"We do not have capacity to run a transparent divestment process.","response":"Sunshine compliance is manageable with a structured calendar: notice postings, public packets, and documented prudence memos. Other funds have followed the same governance playbook without violating open-meeting rules.","citations":["src_california_brown_act","src_gao_public_pension_debrief"],"targets":{"identity":["public_pension"],"audience":["boards","stakeholders"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_settlement-products-are-a-tiny-portion-of-these-companies-revenues', '{"id":"ctr_settlement-products-are-a-tiny-portion-of-these-companies-revenues","type":"counter","claim":"Settlement products are a tiny portion of these companies'' revenues.","response":"Even small revenue lines can signal structural risk: Caterpillar and Israeli banks face lawsuits, exclusions, and credit pressure tied to settlement activity. Screening reallocates capital to resilient issuers.","citations":["src_jury-awards-miller-vs-caterpillar","src_caterpillar-stock-reaction-to-nbim-exclusion","src_moody-s-downgrades-israel"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_the-board-doesn-t-have-authority-to-make-investment-decisions', '{"id":"ctr_the-board-doesn-t-have-authority-to-make-investment-decisions","type":"counter","claim":"The board doesn''t have authority to make investment decisions.","response":"Boards set policy; staff and consultants implement. UC Regents and other fiduciary boards codify exclusions in IPS language while delegating execution to investment teams under defined tracking-error limits.","citations":["src_uc-regents-investment-policy","src_amawi-v-pflugerville-isd-opinion","src_nbim-responsible-investment"],"targets":{"audience":["boards"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_the-situation-is-too-complex-for-binary-decisions', '{"id":"ctr_the-situation-is-too-complex-for-binary-decisions","type":"counter","claim":"The situation is too complex for binary decisions.","response":"Policies can blend process and precision. Evergreen State College defused protests by launching a task force, while Free Money highlights that continued dialogue about ethics and finance is itself part of responsible governance.","citations":["src_evergreen-state-agreement","src_free-money-podcast-divestment-without-dissonance"],"targets":{"audience":["boards","staff","stakeholders","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-creates-legal-liability-litigation-risk', '{"id":"ctr_this-creates-legal-liability-litigation-risk","type":"counter","claim":"This creates legal liability/litigation risk.","response":"Documented policy, credible sourcing, and alignment with fiduciary duties provide cover. Large funds like NBIM and PFZW publish their rationale and have avoided litigation by sticking to evidence-based conduct standards.","citations":["src_nbim-exclusion-decisions","src_pfzw-exclusion-decision","src_koontz-v-watson-opinion"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-distracts-from-our-core-mission', '{"id":"ctr_this-distracts-from-our-core-mission","type":"counter","claim":"This distracts from our core mission.","response":"Mission and finance are intertwined. The Free Money conversation notes divestment debates deepen understanding of finance''s role, while campuses like SFSU tailored policies that supported student learning without derailing institutional objectives.","citations":["src_free-money-podcast-divestment-without-dissonance","src_sfsu-foundation-policy-update"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-is-virtue-signaling-that-won-t-change-anything', '{"id":"ctr_this-is-virtue-signaling-that-won-t-change-anything","type":"counter","claim":"This is virtue signaling that won''t change anything.","response":"Our clients'' signals become shared policy. Every valid alert we receive is researched and added to firm-wide exclusions, and our flagship growth strategy has matched MSCI ACWI while staying values-aligned—so the work is both communal and financially credible.","citations":["src_ethical-capital-investment-screening-policy","src_what-i-d-tell-your-cio-about-divestment"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-politicizes-our-investment-process', '{"id":"ctr_this-politicizes-our-investment-process","type":"counter","claim":"This politicizes our investment process.","response":"Ignoring credible violations is itself a political stance. We use ethical screening as an active risk-management decision—protecting portfolios from legal, reputational, and stranded-asset exposure identified in our screening policy.","citations":["src_ethical-capital-investment-screening-policy","src_us-dol-esg-guidance","src_cuyahoga-county-israel-bond-freeze"],"targets":{"audience":["boards","staff","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-requires-ongoing-monitoring-we-can-t-maintain', '{"id":"ctr_this-requires-ongoing-monitoring-we-can-t-maintain","type":"counter","claim":"This requires ongoing monitoring we can''t maintain.","response":"Set cadence and delegate: many policies mandate annual reviews plus event-driven updates fed by third-party alerts. Consultants and managers absorb the monitoring, reporting back through dashboards.","citations":["src_evergreen-state-agreement","src_msci-esg-focus-methodology","src_nbim-responsible-investment"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-sets-dangerous-precedent-for-future-exclusions', '{"id":"ctr_this-sets-dangerous-precedent-for-future-exclusions","type":"counter","claim":"This sets dangerous precedent for future exclusions.","response":"Institutions already manage multiple ethical screens. UTS and SFSU frame new exclusions as extensions of long-standing SRI policies, proving conduct standards can evolve while staying disciplined.","citations":["src_union-theological-seminary-divestment","src_sfsu-foundation-policy-update"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-sounds-expensive-or-complicated-where-do-i-even-start', '{"id":"ctr_this-sounds-expensive-or-complicated-where-do-i-even-start","type":"counter","claim":"This sounds expensive or complicated—where do I even start?","response":"Ethical screening is messy in practice—that’s why investors hire professionals to map the evidence, translate it into exclusions, and keep the process on track.","citations":["src_ethical-capital-investment-screening-policy"],"targets":{"audience":["staff","consultants","family_friends"],"level":["plain","technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-undermines-academic-freedom-and-exchange', '{"id":"ctr_this-undermines-academic-freedom-and-exchange","type":"counter","claim":"This undermines academic freedom and exchange.","response":"Universities exist for inquiry. Ashby Monk and Kelly Grotke highlight that divestment conversations deepen learning and institutional character; policies can target financial holdings while protecting academic collaboration.","citations":["src_free-money-podcast-divestment-without-dissonance","src_sfsu-foundation-policy-update","src_union-theological-seminary-divestment"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_un-databases-are-politically-motivated-and-unreliable', '{"id":"ctr_un-databases-are-politically-motivated-and-unreliable","type":"counter","claim":"UN databases are politically motivated and unreliable.","response":"Treat activist research as the specialist edge. Ground-level groups like AFSC or the UN database track evidence sooner than generalized ESG vendors; large funds (e.g., KLP) use them as the investigative spine and apply mainstream index tools for implementation.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_klp-divests-settlement-companies","src_ssrn-impact-of-esg-rating-changes"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_we-don-t-have-the-expertise-resources-to-evaluate-these-companies', '{"id":"ctr_we-don-t-have-the-expertise-resources-to-evaluate-these-companies","type":"counter","claim":"We don''t have the expertise/resources to evaluate these companies.","response":"Leverage third-party intelligence and manager tools: AFSC Investigate provides evidence packs, and screened indexes from MSCI/FTSE offer plug-and-play implementation with documented tracking-error budgets.","citations":["src_afsc-investigate","src_msci-esg-indexes-factsheet","src_msci-esg-focus-methodology"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_we-lack-precedent-from-peer-institutions', '{"id":"ctr_we-lack-precedent-from-peer-institutions","type":"counter","claim":"We lack precedent from peer institutions.","response":"Peer action is growing: institutions from SFSU to Union Theological Seminary and pension giants like KLP have adopted universal human-rights screens. They communicate the policy as a continuity of existing SRI principles.","citations":["src_union-theological-seminary-divestment","src_sfsu-foundation-policy-update","src_klp-divests-settlement-companies"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_we-need-engagement-not-divestment', '{"id":"ctr_we-need-engagement-not-divestment","type":"counter","claim":"We need engagement, not divestment.","response":"Escalation ladders work: institutions like the Church of England and Danish pensions engage first, set time-bound objectives, and divest when companies fail to remedy violations—preserving credibility and risk discipline.","citations":["src_church-of-england-pensions-board-divestment","src_akademikerpension-statement","src_nbim-exclusion-decisions"],"targets":{"audience":["boards","staff","consultants","family_friends"],"level":["plain","technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_we-re-a-global-institution-serving-diverse-stakeholders', '{"id":"ctr_we-re-a-global-institution-serving-diverse-stakeholders","type":"counter","claim":"We''re a global institution serving diverse stakeholders.","response":"Global fiduciaries already run conduct screens. NBIM and KLP manage worldwide portfolios while excluding companies tied to occupation abuses, demonstrating universal standards work at scale.","citations":["src_nbim-responsible-investment","src_klp-divests-settlement-companies"],"targets":{"audience":["boards","consultants"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_what-about-china-saudi-arabia-other-countries', '{"id":"ctr_what-about-china-saudi-arabia-other-countries","type":"counter","claim":"What about China/Saudi Arabia/other countries?","response":"Apply one standard everywhere: our policy covers any issuer implicated in grave violations—Israeli, Chinese, American, or otherwise—using international law and third-party evidence as triggers.","citations":["src_un-guiding-principles-on-business-human-rights","src_amnesty-israel-s-system-of-apartheid","src_klp-divests-settlement-companies"],"targets":{"audience":["boards","stakeholders","consultants","family_friends"],"level":["plain","technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_central_bank', '{"id":"guide_central_bank","type":"guide","sections":{"ask":"Clarify mandate limits and adopt best-efforts exclusions with transparent rationale.","implementation":"Constraints consistent with reserve management; avoid market disruption.","reporting":"Public statement of limits and methods; periodic review.","risk":"Mandate-consistent risk posture with minimal deviation from reserve benchmarks."},"targets":{"identity":["central_bank"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_corporate_pension', '{"id":"guide_corporate_pension","type":"guide","sections":{"ask":"Commission a Divestment Exposure Assessment that documents how conduct risk links to financial exposure and captures ERISA prudence.","implementation":"Use external data to build risk dossiers, run tracking error and liability aware modeling, and deliver scenario analysis without breaking the de risking glidepath.","reporting":"Provide a prudence memo, funded status sensitivity tables, and a calendar for policy updates shared with the sponsor treasury team.","risk":"Maintain diversification, document thresholds and override clauses, and align with DOL guidance so the assessment shows control rather than activism."},"targets":{"identity":["corporate_pension"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_endowment', '{"id":"guide_endowment","type":"guide","sections":{"ask":"Mission-aligned exclusion framework with committee governance and stakeholder transparency.","implementation":"Manager constraints or screened indexes; publish rationales; staged implementation.","reporting":"Public rationale per issuer; annual reviews with community communication.","risk":"Maintain diversification and factor integrity; narrow scope to grave conduct."},"targets":{"identity":["endowment"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_foundation', '{"id":"guide_foundation","type":"guide","sections":{"ask":"Program-aligned exclusions that reduce reputational risk while preserving portfolio discipline.","implementation":"Simplified screened index or manager constraints; phased timeline.","reporting":"Public list and brief rationales; annual update.","risk":"Emphasize conduct screens to mitigate drawdowns from controversies."},"targets":{"identity":["foundation"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_government', '{"id":"guide_government","type":"guide","sections":{"ask":"Define issuer screens/approved issuer lists consistent with procurement and governance rules.","implementation":"Apply issuer criteria to government bond portfolios; best-efforts approach with legal review; transparent exceptions process.","reporting":"Publish issuer criteria, approved lists, and update cadence.","risk":"Avoid undue concentration; maintain liquidity and policy compliance."},"targets":{"identity":["government"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_individual', '{"id":"guide_individual","type":"guide","sections":{"ask":"Align personal or household savings with stated values and explain the exclusions in plain language.","implementation":"Use low-cost screened ETFs or managed accounts; document which companies are excluded and why.","reporting":"Keep a simple holdings list and revisit annually or after major portfolio changes.","risk":"Maintain emergency funds, diversification, and low fees while applying exclusions."},"targets":{"identity":["individual"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_insurance', '{"id":"guide_insurance","type":"guide","sections":{"ask":"Adopt narrow conduct screens consistent with ALM and capital/rating requirements.","implementation":"Best-efforts exclusions; vendor screen overlays; maintain liquidity ladder.","reporting":"Regulatory-aware disclosures; internal risk memos.","risk":"Tight tracking-error; preserve liability matching and capital buffers."},"targets":{"identity":["insurance"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_public_pension', '{"id":"guide_public_pension","type":"guide","sections":{"ask":"Consultant memo and manager mandates for conduct-based exclusions; define guardrails.","implementation":"Passive screened index or benchmark-aware constraints; phased divestment to minimize costs; maintain diversification.","reporting":"Quarterly one-page dashboard; publish criteria and exclusion list.","risk":"Tracking-error limit (e.g., ≤30 bps) and factor neutrality by vendor."},"targets":{"identity":["public_pension"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_swf', '{"id":"guide_swf","type":"guide","sections":{"ask":"Adopt conduct-based exclusions with a formal ethics/oversight process and public rationales.","implementation":"Multi-manager mandates with optimization constraints; preserve factor/sector neutrality; staged rebalances.","reporting":"Publish exclusion list and rationale; periodic reviews and appeals mechanism; transparent methodology.","risk":"Explicit tracking-error budget (e.g., 15–30 bps) and central risk model governance."},"targets":{"identity":["swf"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_central_bank_link-divestment-to-credit-and-liquidity-management', '{"id":"kp_central_bank_link-divestment-to-credit-and-liquidity-management","type":"key_point","title":"Link divestment to credit and liquidity management","body":"Credit downgrades and liquidity stress—like the recent Israel bond freeze—provide fiduciary cover for rebalancing into aligned issuers.","citations":["src_moody-s-downgrades-israel","src_cuyahoga-county-israel-bond-freeze"],"targets":{"identity":["central_bank"],"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_central_bank_report-exclusions-like-any-other-governance-control', '{"id":"kp_central_bank_report-exclusions-like-any-other-governance-control","type":"key_point","title":"Report exclusions like any other governance control","body":"Publish criteria, changes, and rationale on a set cadence so oversight bodies and auditors can verify compliance without politicizing the process.","citations":["src_ethical-capital-investment-screening-policy","src_koontz-v-watson-opinion"],"targets":{"identity":["central_bank"],"audience":["boards","staff"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_central_bank_use-approved-issuer-lists-to-stay-compliant', '{"id":"kp_central_bank_use-approved-issuer-lists-to-stay-compliant","type":"key_point","title":"Use approved issuer lists to stay compliant","body":"Define a transparent approved issuer list that aligns with procurement statutes and discloses best-efforts limits for bond purchases.","citations":["src_ethical-capital-investment-screening-policy","src_un-guiding-principles-on-business-human-rights"],"targets":{"identity":["central_bank"],"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_corporate_civil_rights_risk', '{"id":"kp_corporate_civil_rights_risk","type":"key_point","title":"Civil-rights scrutiny requires neutral conduct-based criteria","body":"Advisory Opinion 2025-04A shows the DOL will flag investment programs that could violate federal civil-rights law even when ERISA process is sound. Divestment language must stay geography-neutral, cite objective UN conduct lists, and undergo civil-rights review so opponents cannot reframe the policy as discrimination.","citations":["src_dol_advisory_opinion_2025_04a","src_jdsupra_dol_pulls_racial_equity_guidance"],"targets":{"audience":["boards","consultants","staff"],"level":["technical"],"identity":["corporate_pension"],"motivation":["regulatory_drivers"]},"tags":["compliance","risk_management"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_corporate_db_vs_dc_litigation', '{"id":"kp_corporate_db_vs_dc_litigation","type":"key_point","title":"DB litigation shields do not protect DC lineups","body":"The 2024 dismissal of Wong v. NYCERS rested on DB participants lacking standing because promised benefits are formula-driven. Corporate DC plans lack that protection, so divestment moves need heightened documentation of pecuniary analysis, participant communications, and consultant sign-off to withstand direct performance challenges.","citations":["src_nyc_wong_v_nycers_dismissal","src_us-dol-erisa-overview"],"targets":{"audience":["boards","consultants"],"level":["plain","technical"],"identity":["corporate_pension"],"motivation":["regulatory_drivers"]},"tags":["litigation","compliance"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_corporate_erisa_pecuniary_case', '{"id":"kp_corporate_erisa_pecuniary_case","type":"key_point","title":"Corporate divestment must rest on a pecuniary fiduciary case","body":"ERISA binds corporate plan fiduciaries to act solely for participants and document prudent pecuniary analysis. Divestment policies need written prudence memos, legal counsel review, and consultant opinions that link conduct-based exclusions to sanctions, litigation, and reputational risk so the screen clearly mitigates financial exposure.","citations":["src_us-dol-erisa-overview","src_us-dol-esg-guidance"],"assertions":["assert_corporate_pension_erisa_duty"],"targets":{"audience":["boards","consultants","staff"],"level":["technical"],"identity":["corporate_pension"],"motivation":["regulatory_drivers"]},"tags":["fiduciary","risk_management"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_corporate_implementation_friction', '{"id":"kp_corporate_implementation_friction","type":"key_point","title":"Implementation friction should shape the divestment brief","body":"Consultants warn that pooled funds and illiquid partnerships cannot take bespoke screens overnight. Stage quick wins in liquid accounts, plan full manager searches for pooled mandates, and document trade-offs so the policy scope and timeline match what governance and vendors can actually deliver.","citations":["src_callan_divest_or_engage","src_cambridge_divestment_governance"],"targets":{"audience":["boards","consultants","staff"],"level":["technical"],"identity":["corporate_pension"]},"tags":["implementation","strategy_spectrum"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_corporate_regulatory_pendulum_guardrails', '{"id":"kp_corporate_regulatory_pendulum_guardrails","type":"key_point","title":"Regulatory whiplash demands hard guardrails","body":"DOL guidance has swung from the 2020 financial-factors rule to the 2022 prudence rule and the pending 2025 reversal. The only durable strategy is to embed measurable controls such as tracking-error caps, diversification tests, override logs, and override clauses so the portfolio stays defensible regardless of which interpretation prevails.","citations":["src_us-dol-esg-guidance","src_goodwinlaw_dol_abandons_esg_rule","src_massachusetts_firearm_divestment"],"assertions":["assert_corporate_pension_erisa_duty"],"targets":{"audience":["boards","consultants","staff"],"level":["technical"],"identity":["corporate_pension"],"motivation":["regulatory_drivers"]},"tags":["fiduciary","compliance"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_fiduciary-duty-and-values-alignment', '{"id":"kp_fiduciary-duty-and-values-alignment","type":"key_point","title":"Fiduciary duty and values alignment","body":"Modern fiduciary guidance recognizes that material ESG risks and client objectives can be incorporated without abandoning prudent, return-seeking management.","citations":["src_un-pri-fiduciary-duty-in-the-21st-century","src_us-dol-esg-factors-fact-sheet"],"assertions":["assert_modern_fiduciary_guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_foundation_fiduciary_modern_prudence', '{"id":"kp_foundation_fiduciary_modern_prudence","type":"key_point","title":"Modern prudence standard supports mission-aligned portfolios","body":"IRC §4944 still forbids jeopardizing investments, but Treasury regulations judge prudence at the time of decision and across the whole portfolio. Paired with IRS Notice 2015-62, boards can document mission-driven allocations as ordinary business care when they evaluate risk, return, and charitable purpose in one deliberative process.","citations":["src_irs_jeopardizing_investments","src_irs_notice_2015_62"],"targets":{"identity":["foundation","endowment"],"audience":["boards","consultants","staff"],"level":["technical"],"motivation":["internal_leadership","regulatory_drivers"]},"tags":["fiduciary","mission_alignment"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_foundation_payout_discipline', '{"id":"kp_foundation_payout_discipline","type":"key_point","title":"5% payout is the floor—use divestment to strengthen the base","body":"The mandatory 5% distribution is calculated on the average fair market value of the foundation''s assets. Mission-aligned MRIs can grow corpus value, increasing the dollar amount of future grants, while PRIs recycle returns into new qualifying distributions. Documenting this feedback loop answers worries about eroding purchasing power.","citations":["src_irs_private_foundation_payout"],"targets":{"identity":["foundation","endowment"],"audience":["boards","stakeholders"],"level":["plain"],"motivation":["internal_leadership","external_stakeholders"]},"tags":["payout","communications"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_foundation_pri_mri_toolkit', '{"id":"kp_foundation_pri_mri_toolkit","type":"key_point","title":"PRIs and MRIs give trustees two levers for mission capital","body":"Program-related investments (PRIs) count toward the 5% payout and can recycle charitable capital, while mission-related investments (MRIs) sit in the endowment and pursue market-rate returns. Linking both in the investment policy statement clarifies when the foundation is deploying charitable risk capital versus permanent portfolio assets.","citations":["src_irs_notice_2015_62","src_irs_private_foundation_payout"],"targets":{"identity":["foundation","endowment"],"audience":["boards","consultants","staff"],"level":["technical"],"motivation":["internal_leadership","external_stakeholders"]},"tags":["implementation","impact_investing"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_foundation_upmifa_mission_alignment', '{"id":"kp_foundation_upmifa_mission_alignment","type":"key_point","title":"UPMIFA explicitly embeds mission into prudence analysis","body":"UPMIFA directs fiduciaries to consider the charitable purposes of the institution when setting investment strategy, alongside risk, return, liquidity, and diversification. Referencing the statute in board minutes shows that aligning the endowment with mission is part of prudent oversight—not a deviation from it.","citations":["src_upmifa_text"],"targets":{"identity":["foundation","endowment"],"audience":["boards","staff"],"level":["technical"],"motivation":["internal_leadership"]},"tags":["governance","policy"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_government_link-divestment-to-credit-and-liquidity-management', '{"id":"kp_government_link-divestment-to-credit-and-liquidity-management","type":"key_point","title":"Link divestment to credit and liquidity management","body":"Credit downgrades and liquidity stress—like the recent Israel bond freeze—provide fiduciary cover for rebalancing into aligned issuers.","citations":["src_moody-s-downgrades-israel","src_cuyahoga-county-israel-bond-freeze"],"targets":{"identity":["government"],"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_government_report-exclusions-like-any-other-governance-control', '{"id":"kp_government_report-exclusions-like-any-other-governance-control","type":"key_point","title":"Report exclusions like any other governance control","body":"Publish criteria, changes, and rationale on a set cadence so oversight bodies and auditors can verify compliance without politicizing the process.","citations":["src_ethical-capital-investment-screening-policy","src_koontz-v-watson-opinion"],"targets":{"identity":["government"],"audience":["boards","staff"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_government_use-approved-issuer-lists-to-stay-compliant', '{"id":"kp_government_use-approved-issuer-lists-to-stay-compliant","type":"key_point","title":"Use approved issuer lists to stay compliant","body":"Define a transparent approved issuer list that aligns with procurement statutes and discloses best-efforts limits for bond purchases.","citations":["src_ethical-capital-investment-screening-policy","src_un-guiding-principles-on-business-human-rights"],"targets":{"identity":["government"],"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_individual_specialized-intelligence-catches-what-esg-ratings-miss', '{"id":"kp_individual_specialized-intelligence-catches-what-esg-ratings-miss","type":"key_point","title":"Specialized intelligence catches what ESG ratings miss","body":"On-the-ground researchers (AFSC, UN) surface settlement and occupation ties long before generalized ESG vendors adjust their scores. Pair activist data for precision with mainstream index tools for implementation.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_ssrn-esg-ratings-divergence"],"targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_individual_start-with-your-own-accounts', '{"id":"kp_individual_start-with-your-own-accounts","type":"key_point","title":"Start with your own accounts","body":"List holdings in retirement and brokerage accounts, then shift into screened ETFs or managed accounts that match your risk profile while keeping emergency reserves intact.","citations":["src_us-dol-esg-guidance","src_nyu-stern-esg-and-financial-performance"],"targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_individual_use-the-screened-options-your-custodian-already-offers', '{"id":"kp_individual_use-the-screened-options-your-custodian-already-offers","type":"key_point","title":"Use the screened options your custodian already offers","body":"Most platforms list values-screened ETFs and separate-account providers. Switching allocations usually takes minutes once you have the tickers and minimums.","citations":["src_msci-esg-indexes-factsheet","src_ftse-russell-squaring-the-circle-on-carbon-track"],"targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_insurance_alm_capital_efficiency', '{"id":"kp_insurance_alm_capital_efficiency","type":"key_point","title":"Divestment unlocks ALM and capital efficiency","body":"ALM teams can convert negative convexity positions and long asset-duration gaps into capital-efficient portfolios by selling high charge assets and redeploying into duration-matched replacements. The move raises return on required capital while shrinking C-3 interest rate charges.","citations":["src_conning_insurance_saa_2017","src_aaa_liquidity_risk_management_2024"],"targets":{"identity":["insurance"],"audience":["boards","staff","consultants"],"level":["technical"],"motivation":["internal_leadership","regulatory_drivers"]},"tags":["alm","capital"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_insurance_esg_climate_pressure', '{"id":"kp_insurance_esg_climate_pressure","type":"key_point","title":"Climate supervision accelerates conduct-based divestment","body":"Supervisors are embedding climate and ESG risk into solvency reviews. ORSA, NAIC climate surveys, and emerging EU-style due diligence regimes incentivize insurers to exit high-carbon exposures and document the shift as prudent risk management rather than values-driven activism.","citations":["src_eiopa_equity_drivers_2018","src_debevoise_naic_rbc_task_force"],"targets":{"identity":["insurance"],"audience":["boards","staff","consultants"],"level":["technical"],"motivation":["external_stakeholders","regulatory_drivers"]},"tags":["esg","climate"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_insurance_implementation_ladder', '{"id":"kp_insurance_implementation_ladder","type":"key_point","title":"Implementation ladder ranges from overlays to impact sleeves","body":"Insurers can stage divestment through a ladder: credit overlays to synthetically exit exposures, bespoke mandates or sidecars to outsource risk, full block sales to transfer liabilities, and mission-aligned private market sleeves that redeploy capital into justice-focused projects.","citations":["src_artemis_sidecar_surge_2025","src_caia_investment_committee_2024","src_deloitte_insurer_divestment_2021"],"targets":{"identity":["insurance"],"audience":["boards","staff","consultants"],"level":["technical"],"motivation":["internal_leadership","external_stakeholders","regulatory_drivers"]},"tags":["implementation","strategy_spectrum"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_insurance_rating_agency_alignment', '{"id":"kp_insurance_rating_agency_alignment","type":"key_point","title":"Rating agency dialogue should lead the transaction","body":"Moody''s, S&P, Fitch, and AM Best all translate divestment into capital adequacy, risk profile, and liquidity metrics. Deliver pro-forma models that show reduced high-risk assets, higher capital redundancy, and improved liquidity coverage so analysts can upgrade—or at least protect—the financial strength rating.","citations":["src_deloitte_insurer_divestment_2021","src_artemis_sidecar_surge_2025"],"targets":{"identity":["insurance"],"audience":["boards","consultants"],"level":["technical"],"motivation":["external_stakeholders","regulatory_drivers"]},"tags":["rating_agencies","communications"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_insurance_regulatory_guardrails', '{"id":"kp_insurance_regulatory_guardrails","type":"key_point","title":"Regulatory guardrails turn divestment into a solvency tool","body":"SAP restricts insurers to admitted assets, RBC assigns capital charges to every risk bucket, and ORSA demands a narrative showing resilience under stress. Proactive divestment from capital-inefficient holdings boosts statutory surplus, improves RBC ratios, and gives boards a defensible ORSA story before regulators ask for it.","citations":["src_debevoise_naic_rbc_task_force","src_aaa_liquidity_risk_management_2024","src_chicago_fed_life_safety_net_2024"],"targets":{"identity":["insurance"],"audience":["boards","staff","consultants"],"level":["technical"],"motivation":["regulatory_drivers"]},"tags":["regulation","risk_management"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_outcome_identity_alignment', '{"id":"kp_outcome_identity_alignment","type":"key_point","title":"Outcome: investments align with identity","body":"Clear divestment criteria anchor portfolios in the institution’s stated ethics, letting trustees and staff demonstrate that capital flows reflect mission and fiduciary commitments. Alignment messaging reassures beneficiaries, donors, and community partners that investment actions match identity.","citations":["src_ethical-capital-screening-policy"],"assertions":["assert_modern_fiduciary_guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"],"identity":["public_pension","corporate_pension","government","central_bank","swf","insurance","endowment","foundation"]},"tags":["outcome","strategy_spectrum"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_outcome_mission_trust', '{"id":"kp_outcome_mission_trust","type":"key_point","title":"Outcome: mission trust reinforced","body":"When boards drive divestment, they can show donors, students, and program staff that capital decisions back institutional values without jeopardising spending policies. Transparent trade plans and impact dashboards keep leadership, faculty, and community stakeholders aligned.","citations":["src_ethical-capital-screening-policy"],"assertions":["assert_endowment_spending_norms"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"],"identity":["endowment","foundation"],"motivation":["internal_leadership"]},"tags":["outcome","motivation_internal"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_outcome_regulatory_confidence', '{"id":"kp_outcome_regulatory_confidence","type":"key_point","title":"Outcome: documented compliance confidence","body":"Conduct-based exclusions, optimization reports, and counsel sign-offs create an audit trail that satisfies fiduciary and statutory obligations. Regulators and attorneys general see proactive risk management, lowering litigation exposure and procurement challenges.","citations":["src_un-guiding-principles-on-business-human-rights","src_koontz-v-watson-opinion"],"assertions":["assert_modern_fiduciary_guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"identity":["public_pension","corporate_pension","government","central_bank","swf","insurance"],"motivation":["regulatory_drivers"]},"tags":["outcome","motivation_regulatory"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_outcome_stakeholder_momentum', '{"id":"kp_outcome_stakeholder_momentum","type":"key_point","title":"Outcome: stakeholder momentum sustained","body":"Coalitions see tangible follow-through when portfolios exit targeted issuers and capital is redeployed toward community priorities. Publishing progress dashboards and reinvestment pipelines keeps organizers engaged and channels pressure into ongoing governance improvements.","citations":["src_afsc-investigate"],"assertions":["assert_public_pension_transparency_governance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"],"identity":["public_pension","government","endowment","foundation"],"motivation":["external_stakeholders"]},"tags":["outcome","motivation_external"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_professional-exclusions-maintain-benchmark-like-performance', '{"id":"kp_professional-exclusions-maintain-benchmark-like-performance","type":"key_point","title":"Professional exclusions maintain benchmark-like performance","body":"Large public funds implement conduct-based exclusions and continue to deliver returns close to their benchmarks by rebalancing and preserving factor exposures.","citations":["src_nbim-responsible-investment","src_nbim-exclusion-of-companies"],"assertions":["assert_benchmark_grade_exclusions"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_anti_esg_compliance', '{"id":"kp_public_anti_esg_compliance","type":"key_point","title":"Anti-ESG statutes narrow pecuniary factors—but allow risk framing","body":"Laws such as Texas SB13 and similar provisions restrict investment decisions that appear political. Fiduciaries can still act when they demonstrate material risk—sanctions, litigation, stranded assets—and maintain a compliance log tracking boycott lists and exemptions.","citations":["src_texas_sb13","src_texas_comptroller_energy_boycott_list"],"targets":{"identity":["public_pension"],"audience":["boards","staff","consultants"],"level":["technical"],"motivation":["regulatory_drivers","external_stakeholders"]},"tags":["regulation","compliance"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_fiduciary_exclusive_purpose', '{"id":"kp_public_fiduciary_exclusive_purpose","type":"key_point","title":"Exclusive-purpose clauses anchor divestment decisions","body":"State constitutions and statutes—from California''s Article XVI to Ohio''s §145.11—make the duty to participants paramount. Conduct-based divestment must be framed as protecting beneficiaries from legal, financial, or operational risk, not as a political gesture.","citations":["src_california_const_article_xvi","src_ohio_rc_145_11"],"targets":{"identity":["public_pension"],"audience":["boards","staff","consultants"],"level":["technical"],"motivation":["regulatory_drivers"]},"tags":["fiduciary","policy"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_pension_consultants-can-document-explicit-guardrails', '{"id":"kp_public_pension_consultants-can-document-explicit-guardrails","type":"key_point","title":"Consultants can document explicit guardrails","body":"Ask your consultant to publish tracking-error budgets, factor controls, and rebalancing cadence so trustees see how exclusions stay benchmark-aware.","citations":["src_msci-esg-indexes-factsheet","src_church-of-england-pensions-board-divestment"],"targets":{"identity":["public_pension"],"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_pension_peer-funds-already-run-conduct-screens', '{"id":"kp_public_pension_peer-funds-already-run-conduct-screens","type":"key_point","title":"Peer funds already run conduct screens","body":"KLP, AkademikerPension, and PFZW have implemented occupation-related exclusions with neutral-to-positive performance impact—use their public reporting as precedent.","citations":["src_klp-divests-settlement-companies","src_akademikerpension-statement","src_pfzw-exclusion-decision"],"targets":{"identity":["public_pension"],"audience":["boards","staff","consultants"],"level":["plain"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_pension_specialized-intelligence-catches-what-esg-ratings-miss', '{"id":"kp_public_pension_specialized-intelligence-catches-what-esg-ratings-miss","type":"key_point","title":"Specialized intelligence catches what ESG ratings miss","body":"AFSC and UN research identify occupation-linked issuers early; combine their insight with customization from mainstream vendors to deliver results consultants can monitor.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_ssrn-esg-ratings-divergence"],"targets":{"identity":["public_pension"],"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_procedural_prudence', '{"id":"kp_public_procedural_prudence","type":"key_point","title":"Procedural prudence is the best defense","body":"Courts look at process—documented analysis, expert input, and clear rationale—not just outcomes. Maintaining detailed minutes, prudence memos, and consultant opinions turns a divestment decision into evidence of prudence if it is ever challenged.","citations":["src_gao_public_pension_debrief"],"targets":{"identity":["public_pension"],"audience":["boards","staff","consultants"],"level":["technical"],"motivation":["regulatory_drivers"]},"tags":["governance","documentation"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_transparency_requirements', '{"id":"kp_public_transparency_requirements","type":"key_point","title":"Sunshine laws require public choreography","body":"Open-meeting statutes like the Brown Act dictate timelines for notice, agendas, and public comment. Successful divestment campaigns plan resolutions, RFPs, and consultant presentations so they meet statutory posting windows and withstand scrutiny.","citations":["src_california_brown_act"],"targets":{"identity":["public_pension"],"audience":["boards","staff"],"level":["technical"],"motivation":["external_stakeholders","regulatory_drivers"]},"tags":["transparency","process"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_specialized-intelligence-catches-what-esg-ratings-miss', '{"id":"kp_specialized-intelligence-catches-what-esg-ratings-miss","type":"key_point","title":"Specialized intelligence catches what ESG ratings miss","body":"On-the-ground researchers (AFSC, UN) surface settlement and occupation ties long before generalized ESG vendors adjust their scores. Pair activist data for precision with MSCI/FTSE tools for delivery, so implementation stays repeatable.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_ssrn-esg-ratings-divergence"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_decision_triggers', '{"id":"kp_spectrum_decision_triggers","type":"key_point","title":"Escalation triggers move institutions up the ladder","body":"Failed engagements, accelerator protest wins, and new compliance signals (like DOL civil-rights advisories) are the events boards can document to justify stepping from stewardship into screening or divestment.","citations":["src_cambridge_divestment_governance","src_irish_times_trinity_divest","src_dol_advisory_opinion_2025_04a"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"],"motivation":["regulatory_drivers","internal_leadership","external_stakeholders"]},"tags":["strategy_spectrum","governance"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_portfolio_integration', '{"id":"kp_spectrum_portfolio_integration","type":"key_point","title":"Portfolio plumbing must match the chosen tier","body":"Whichever rung you pick, refresh manager RFPs, build screened benchmarks, and extend risk reports so tracking error, impact metrics, and override logs show up alongside returns.","citations":["src_callan_divest_or_engage","src_msci-esg-index-construction","src_cambridge_divestment_governance"],"targets":{"audience":["boards","consultants","staff"],"level":["technical"],"motivation":["regulatory_drivers","internal_leadership","external_stakeholders"]},"tags":["implementation","portfolio_construction"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_tier1_monitoring', '{"id":"kp_spectrum_tier1_monitoring","type":"key_point","title":"Tier 1: Monitoring builds the human-rights risk map","body":"Start by piping specialist human-rights datasets into the portfolio risk stack to flag controversies and liability exposure without touching allocations. Treat AFSC investigative data and MSCI controversy scores like any other risk factor so trustees can see where holdings diverge from policy goals.","citations":["src_msci-esg-indexes-methodology","src_afsc-investigate"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"],"motivation":["regulatory_drivers","internal_leadership","external_stakeholders"]},"tags":["implementation","strategy_spectrum"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_tier2_stewardship', '{"id":"kp_spectrum_tier2_stewardship","type":"key_point","title":"Tier 2: Stewardship demands an escalation ladder","body":"Document a stewardship policy that names priority human-rights asks, assigns owners for company dialogue, and spells out when escalation flips from private meetings to proxy votes, public statements, and director-withhold campaigns.","citations":["src_callan_divest_or_engage","src_cambridge_divestment_governance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"motivation":["regulatory_drivers","internal_leadership","external_stakeholders"]},"tags":["implementation","governance"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_tier3_targeted_screen', '{"id":"kp_spectrum_tier3_targeted_screen","type":"key_point","title":"Tier 3: Targeted screens make the first portfolio cut","body":"Narrow exclusion lists anchored in UN or AFSC criteria let boards codify red lines, publish restricted issuers, and run performance against a custom screened benchmark so the policy impact stays auditable.","citations":["src_un-database-on-settlements","src_afsc-investigate"],"targets":{"audience":["boards","consultants","staff"],"level":["technical"],"motivation":["regulatory_drivers","internal_leadership","external_stakeholders"]},"tags":["implementation","portfolio_construction"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_tier4_thematic_positive', '{"id":"kp_spectrum_tier4_thematic_positive","type":"key_point","title":"Tier 4: Positive and thematic tilts show what you fund","body":"Allocating sleeves to human-rights solution themes and best-in-class issuers reframes the story from avoidance to proactive impact, backed by factor-aware ESG construction.","citations":["src_msci-esg-index-construction","src_oxford-sustainable-finance-divestment-research"],"targets":{"audience":["boards","consultants","staff"],"level":["technical"],"motivation":["internal_leadership","external_stakeholders"]},"tags":["implementation","strategy_spectrum"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_tier5_full_divestment', '{"id":"kp_spectrum_tier5_full_divestment","type":"key_point","title":"Tier 5: Full divestment delivers the hard break","body":"When boards adopt conduct-based divestment statutes, publish the prohibited list, and stage the sell-down, they provide unmistakable proof that capital no longer backs the targeted harm and that policy guardrails can survive public scrutiny.","citations":["src_irish_times_trinity_divest","src_sfsu-foundation-policy-update"],"targets":{"audience":["boards","consultants","staff"],"level":["plain","technical"],"motivation":["internal_leadership","external_stakeholders"]},"tags":["implementation","strategy_spectrum"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_tier6_impact_reinvestment', '{"id":"kp_spectrum_tier6_impact_reinvestment","type":"key_point","title":"Tier 6: Impact carve-outs channel capital to affected communities","body":"Dedicated private-market sleeves can finance Palestinian-led enterprises or justice-aligned infrastructure, pairing IRIS+ metrics with return targets so trustees see both impact and cash flow.","citations":["src_rockefeller_brothers_divestment_review","src_free-money-podcast-divestment-without-dissonance"],"targets":{"audience":["boards","consultants","staff"],"level":["technical"],"motivation":["internal_leadership","external_stakeholders"]},"tags":["implementation","impact_investing"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_spectrum_tier7_mission_partnerships', '{"id":"kp_spectrum_tier7_mission_partnerships","type":"key_point","title":"Tier 7: Mission-native partnerships align the whole enterprise","body":"Building long-term partnerships with funds and founders whose sole mandate is justice turns the portfolio into a mission delivery system; it demands patient capital, board conviction, and narrative-rich reporting.","citations":["src_nathan_cummings_mission_alignment","src_free-money-podcast-divestment-without-dissonance"],"targets":{"audience":["boards","staff"],"level":["plain","technical"],"motivation":["internal_leadership","external_stakeholders"]},"tags":["implementation","impact_investing"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_strategy_external_stakeholders', '{"id":"kp_strategy_external_stakeholders","type":"key_point","title":"External stakeholders: convert campaign pressure into structured capital moves","body":"Strategy spectrum: responsive divest-reinvest mandates, coalition-built exclusion lists, community reinvestment pools, and transparency dashboards that track follow-through.\nImplementation notes: co-design criteria with organizers, publish escalation ladders, and coordinate communications with governance, donor, and beneficiary groups.\nOutcomes to monitor: stakeholder sentiment shifts, media/regulator responses, capital redeployment volumes, and engagement opportunities created by divestment decisions.","citations":["src_afsc-investigate","src_naacp-v-claiborne-hardware-458-u-s-886"],"assertions":["assert_public_pension_transparency_governance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"identity":["public_pension","government","endowment","foundation","individual"],"motivation":["external_stakeholders"]},"tags":["strategy_spectrum","motivation_external"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_strategy_internal_leadership', '{"id":"kp_strategy_internal_leadership","type":"key_point","title":"Internal leadership: align mission and capital without losing discipline","body":"Strategy spectrum: mission-screen overlays, carve-out impact sleeves, delegated investment committee mandates, and donor-aligned policy statements with phased implementation schedules.\nImplementation notes: connect divestment criteria to charter language, donor restrictions, and campus/program goals; pilot with separate accounts or manager guidelines before system-wide rollout.\nOutcomes to monitor: mission KPI dashboards, spending policy resilience, peer benchmarking, and qualitative feedback loops from leadership and program staff.","citations":["src_ethical-capital-screening-policy","src_nacubo_tiaa_spending"],"assertions":["assert_endowment_spending_norms"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"identity":["endowment","foundation","individual"],"motivation":["internal_leadership"]},"tags":["strategy_spectrum","motivation_internal"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_strategy_regulatory_drivers', '{"id":"kp_strategy_regulatory_drivers","type":"key_point","title":"Regulatory drivers: codify compliance-first divestment","body":"Strategy spectrum: conduct-based exclusion lists, optimized overlays on core benchmarks, or segregated compliance sleeves to satisfy fiduciary and statutory directives.\nImplementation notes: lean on attorney general guidance, procurement statutes, and consultant attestations; pair exclusions with tracking-error budgets and factor neutrality tests.\nOutcomes to monitor: variance vs policy benchmark (<50 bps TE targets), documented liability risk reduction, regulator-ready audit trails, and stakeholder briefings that underline prudence.","citations":["src_un-guiding-principles-on-business-human-rights","src_koontz-v-watson-opinion","src_amawi-v-pflugerville-isd-opinion"],"assertions":["assert_tracking_error_controls","assert_modern_fiduciary_guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"identity":["public_pension","corporate_pension","government","central_bank","swf","insurance"],"motivation":["regulatory_drivers"]},"tags":["strategy_spectrum","motivation_regulatory"]}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_tracking-error-and-factor-neutrality', '{"id":"kp_tracking-error-and-factor-neutrality","type":"key_point","title":"Tracking error and factor neutrality","body":"Exclusions can be implemented with explicit tracking-error budgets and factor controls so the portfolio stays close to its benchmark while meeting policy goals.","citations":["src_msci-esg-indexes-methodology","src_ftse-russell-squaring-the-circle-on-carbon-track"],"assertions":["assert_tracking_error_controls"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'note_cio', '{"id":"note_cio","type":"template_snippet","title":"CIO Implementation Note","markdown":"Implementation focuses on preserving benchmark alignment: specify a tracking-error budget, maintain factor neutrality, use vendor screened indexes or optimization constraints, and phase trades to avoid divestment shocks.","targets":{"audience":["fiduciary"]}}') ON CONFLICT(version, id) DO UPDATE SET payload=excluded.payload;
