INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_generic', '{"id":"opener_generic","type":"opener","text":"We can align investments with basic human rights using professional, benchmark-aware implementation. Large institutions already use narrowly scoped exclusions while maintaining diversification and risk controls.","targets":{"audience":["stakeholders"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_individual_one_on_one', '{"id":"opener_individual_one_on_one","type":"opener","text":"For friends or family, emphasize aligning personal savings with shared values and keep the steps concrete and jargon-free.","targets":{"identity":["individual"],"venue":["one_on_one"],"audience":["family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_individual_base', '{"id":"opener_individual_base","type":"opener","text":"Individual investors can align personal or household savings with their values using low-cost screened funds while keeping diversification and emergency reserves intact.","targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_swf_base', '{"id":"opener_swf_base","type":"opener","text":"A sovereign fund can implement conduct-based exclusions via a formal ethics process, public rationales, and factor-neutral optimization with explicit tracking-error budgets.","targets":{"identity":["swf"],"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_public_pension_town_meeting', '{"id":"opener_public_pension_town_meeting","type":"opener","text":"Public pensions can adopt a clear exclusion policy with consultant support, preserve diversification, and report progress without sacrificing fiduciary discipline.","targets":{"identity":["public_pension"],"venue":["town_meeting"],"audience":["stakeholders"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_public_pension_base', '{"id":"opener_public_pension_base","type":"opener","text":"Public plans can use consultant/manager mandates, set a tracking-error guardrail, and phase implementation while keeping beneficiaries’ risk/return in focus.","targets":{"identity":["public_pension"],"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_corporate_pension_base', '{"id":"opener_corporate_pension_base","type":"opener","text":"Corporate plans can update the IPS, adopt a screened index or constraints with minimal overhead, and maintain de-risking alignment.","targets":{"identity":["corporate_pension"],"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_endowment_school_board', '{"id":"opener_endowment_school_board","type":"opener","text":"An endowment can reflect mission and educational values with transparent criteria and periodic reviews.","targets":{"identity":["endowment"],"venue":["school_board"],"audience":["boards"],"level":["plain"]}}');
