INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_foundation_base', '{"id":"opener_foundation_base","type":"opener","text":"Foundations can align programs and portfolios, reduce reputational risk through clear criteria, and use simplified implementation paths.","targets":{"identity":["foundation"],"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_insurance_base', '{"id":"opener_insurance_base","type":"opener","text":"Insurance portfolios can adopt narrow conduct screens consistent with ALM and capital/rating constraints, emphasizing best-efforts and risk integrity.","targets":{"identity":["insurance"],"audience":["boards"],"level":["plain","technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_central_bank_base', '{"id":"opener_central_bank_base","type":"opener","text":"Official institutions can clarify mandate limits, apply best-efforts exclusions, and provide transparent rationales and constraints.","targets":{"identity":["central_bank"],"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_government_city_council', '{"id":"opener_government_city_council","type":"opener","text":"For government bond portfolios, define approved issuer lists and issuer screens consistent with procurement rules and transparency.","targets":{"identity":["government"],"venue":["city_council"],"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_government_base', '{"id":"opener_government_base","type":"opener","text":"Government investors (city/county/state) can apply issuer screens/approved lists for bonds, observe procurement/governance limits, and report with best-efforts language.","targets":{"identity":["government"],"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_individual', '{"id":"guide_individual","type":"guide","sections":{"ask":"Align personal or household savings with stated values and explain the exclusions in plain language.","implementation":"Use low-cost screened ETFs or managed accounts; document which companies are excluded and why.","reporting":"Keep a simple holdings list and revisit annually or after major portfolio changes.","risk":"Maintain emergency funds, diversification, and low fees while applying exclusions."},"targets":{"identity":["individual"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_swf', '{"id":"guide_swf","type":"guide","sections":{"ask":"Adopt conduct-based exclusions with a formal ethics/oversight process and public rationales.","implementation":"Multi-manager mandates with optimization constraints; preserve factor/sector neutrality; staged rebalances.","reporting":"Publish exclusion list and rationale; periodic reviews and appeals mechanism; transparent methodology.","risk":"Explicit tracking-error budget (e.g., 15–30 bps) and central risk model governance."},"targets":{"identity":["swf"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_public_pension', '{"id":"guide_public_pension","type":"guide","sections":{"ask":"Consultant memo and manager mandates for conduct-based exclusions; define guardrails.","implementation":"Passive screened index or benchmark-aware constraints; phased divestment to minimize costs; maintain diversification.","reporting":"Quarterly one-page dashboard; publish criteria and exclusion list.","risk":"Tracking-error limit (e.g., ≤30 bps) and factor neutrality by vendor."},"targets":{"identity":["public_pension"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_corporate_pension', '{"id":"guide_corporate_pension","type":"guide","sections":{"ask":"Update IPS to reflect exclusions consistent with sponsor objectives.","implementation":"Low-overhead screened index; use vendor methodologies; minimize turnover and costs.","reporting":"Periodic sponsor reporting; document rationales and methods.","risk":"Maintain glidepath/de-risking; narrow scope to avoid mandate conflicts."},"targets":{"identity":["corporate_pension"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_endowment', '{"id":"guide_endowment","type":"guide","sections":{"ask":"Mission-aligned exclusion framework with committee governance and stakeholder transparency.","implementation":"Manager constraints or screened indexes; publish rationales; staged implementation.","reporting":"Public rationale per issuer; annual reviews with community communication.","risk":"Maintain diversification and factor integrity; narrow scope to grave conduct."},"targets":{"identity":["endowment"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_foundation', '{"id":"guide_foundation","type":"guide","sections":{"ask":"Program-aligned exclusions that reduce reputational risk while preserving portfolio discipline.","implementation":"Simplified screened index or manager constraints; phased timeline.","reporting":"Public list and brief rationales; annual update.","risk":"Emphasize conduct screens to mitigate drawdowns from controversies."},"targets":{"identity":["foundation"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_insurance', '{"id":"guide_insurance","type":"guide","sections":{"ask":"Adopt narrow conduct screens consistent with ALM and capital/rating requirements.","implementation":"Best-efforts exclusions; vendor screen overlays; maintain liquidity ladder.","reporting":"Regulatory-aware disclosures; internal risk memos.","risk":"Tight tracking-error; preserve liability matching and capital buffers."},"targets":{"identity":["insurance"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_central_bank', '{"id":"guide_central_bank","type":"guide","sections":{"ask":"Clarify mandate limits and adopt best-efforts exclusions with transparent rationale.","implementation":"Constraints consistent with reserve management; avoid market disruption.","reporting":"Public statement of limits and methods; periodic review.","risk":"Mandate-consistent risk posture with minimal deviation from reserve benchmarks."},"targets":{"identity":["central_bank"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'guide_government', '{"id":"guide_government","type":"guide","sections":{"ask":"Define issuer screens/approved issuer lists consistent with procurement and governance rules.","implementation":"Apply issuer criteria to government bond portfolios; best-efforts approach with legal review; transparent exceptions process.","reporting":"Publish issuer criteria, approved lists, and update cadence.","risk":"Avoid undue concentration; maintain liquidity and policy compliance."},"targets":{"identity":["government"]}}');
