INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_specialized-intelligence-catches-what-esg-ratings-miss', '{"id":"kp_specialized-intelligence-catches-what-esg-ratings-miss","type":"key_point","title":"Specialized intelligence catches what ESG ratings miss","body":"On-the-ground researchers (AFSC, UN) surface settlement and occupation ties long before generalized ESG vendors adjust their scores. Pair activist data for precision with MSCI/FTSE tools for delivery, so implementation stays repeatable.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_ssrn-esg-ratings-divergence"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_professional-exclusions-maintain-benchmark-like-performance', '{"id":"kp_professional-exclusions-maintain-benchmark-like-performance","type":"key_point","title":"Professional exclusions maintain benchmark-like performance","body":"Large public funds implement conduct-based exclusions and continue to deliver returns close to their benchmarks by rebalancing and preserving factor exposures.","citations":["src_nbim-responsible-investment","src_nbim-exclusion-of-companies"],"assertions":["assert_benchmark_grade_exclusions"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_tracking-error-and-factor-neutrality', '{"id":"kp_tracking-error-and-factor-neutrality","type":"key_point","title":"Tracking error and factor neutrality","body":"Exclusions can be implemented with explicit tracking-error budgets and factor controls so the portfolio stays close to its benchmark while meeting policy goals.","citations":["src_msci-esg-indexes-methodology","src_ftse-russell-squaring-the-circle-on-carbon-track"],"assertions":["assert_tracking_error_controls"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_fiduciary-duty-and-values-alignment', '{"id":"kp_fiduciary-duty-and-values-alignment","type":"key_point","title":"Fiduciary duty and values alignment","body":"Modern fiduciary guidance recognizes that material ESG risks and client objectives can be incorporated without abandoning prudent, return-seeking management.","citations":["src_un-pri-fiduciary-duty-in-the-21st-century","src_us-dol-esg-factors-fact-sheet"],"assertions":["assert_modern_fiduciary_guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_individual_start-with-your-own-accounts', '{"id":"kp_individual_start-with-your-own-accounts","type":"key_point","title":"Start with your own accounts","body":"List holdings in retirement and brokerage accounts, then shift into screened ETFs or managed accounts that match your risk profile while keeping emergency reserves intact.","citations":["src_us-dol-esg-guidance","src_nyu-stern-esg-and-financial-performance"],"targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_individual_specialized-intelligence-catches-what-esg-ratings-miss', '{"id":"kp_individual_specialized-intelligence-catches-what-esg-ratings-miss","type":"key_point","title":"Specialized intelligence catches what ESG ratings miss","body":"On-the-ground researchers (AFSC, UN) surface settlement and occupation ties long before generalized ESG vendors adjust their scores. Pair activist data for precision with mainstream index tools for implementation.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_ssrn-esg-ratings-divergence"],"targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_individual_use-the-screened-options-your-custodian-already-offers', '{"id":"kp_individual_use-the-screened-options-your-custodian-already-offers","type":"key_point","title":"Use the screened options your custodian already offers","body":"Most platforms list values-screened ETFs and separate-account providers. Switching allocations usually takes minutes once you have the tickers and minimums.","citations":["src_msci-esg-indexes-factsheet","src_ftse-russell-squaring-the-circle-on-carbon-track"],"targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_pension_consultants-can-document-explicit-guardrails', '{"id":"kp_public_pension_consultants-can-document-explicit-guardrails","type":"key_point","title":"Consultants can document explicit guardrails","body":"Ask your consultant to publish tracking-error budgets, factor controls, and rebalancing cadence so trustees see how exclusions stay benchmark-aware.","citations":["src_msci-esg-indexes-factsheet","src_church-of-england-pensions-board-divestment"],"targets":{"identity":["public_pension"],"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_pension_specialized-intelligence-catches-what-esg-ratings-miss', '{"id":"kp_public_pension_specialized-intelligence-catches-what-esg-ratings-miss","type":"key_point","title":"Specialized intelligence catches what ESG ratings miss","body":"AFSC and UN research identify occupation-linked issuers early; combine their insight with customization from mainstream vendors to deliver results consultants can monitor.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_ssrn-esg-ratings-divergence"],"targets":{"identity":["public_pension"],"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_public_pension_peer-funds-already-run-conduct-screens', '{"id":"kp_public_pension_peer-funds-already-run-conduct-screens","type":"key_point","title":"Peer funds already run conduct screens","body":"KLP, AkademikerPension, and PFZW have implemented occupation-related exclusions with neutral-to-positive performance impact—use their public reporting as precedent.","citations":["src_klp-divests-settlement-companies","src_akademikerpension-statement","src_pfzw-exclusion-decision"],"targets":{"identity":["public_pension"],"audience":["boards","staff","consultants"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_government_use-approved-issuer-lists-to-stay-compliant', '{"id":"kp_government_use-approved-issuer-lists-to-stay-compliant","type":"key_point","title":"Use approved issuer lists to stay compliant","body":"Define a transparent approved issuer list that aligns with procurement statutes and discloses best-efforts limits for bond purchases.","citations":["src_ethical-capital-investment-screening-policy","src_un-guiding-principles-on-business-human-rights"],"targets":{"identity":["government"],"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_government_report-exclusions-like-any-other-governance-control', '{"id":"kp_government_report-exclusions-like-any-other-governance-control","type":"key_point","title":"Report exclusions like any other governance control","body":"Publish criteria, changes, and rationale on a set cadence so oversight bodies and auditors can verify compliance without politicizing the process.","citations":["src_ethical-capital-investment-screening-policy","src_koontz-v-watson-opinion"],"targets":{"identity":["government"],"audience":["boards","staff"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_government_link-divestment-to-credit-and-liquidity-management', '{"id":"kp_government_link-divestment-to-credit-and-liquidity-management","type":"key_point","title":"Link divestment to credit and liquidity management","body":"Credit downgrades and liquidity stress—like the recent Israel bond freeze—provide fiduciary cover for rebalancing into aligned issuers.","citations":["src_moody-s-downgrades-israel","src_cuyahoga-county-israel-bond-freeze"],"targets":{"identity":["government"],"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_central_bank_use-approved-issuer-lists-to-stay-compliant', '{"id":"kp_central_bank_use-approved-issuer-lists-to-stay-compliant","type":"key_point","title":"Use approved issuer lists to stay compliant","body":"Define a transparent approved issuer list that aligns with procurement statutes and discloses best-efforts limits for bond purchases.","citations":["src_ethical-capital-investment-screening-policy","src_un-guiding-principles-on-business-human-rights"],"targets":{"identity":["central_bank"],"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_central_bank_report-exclusions-like-any-other-governance-control', '{"id":"kp_central_bank_report-exclusions-like-any-other-governance-control","type":"key_point","title":"Report exclusions like any other governance control","body":"Publish criteria, changes, and rationale on a set cadence so oversight bodies and auditors can verify compliance without politicizing the process.","citations":["src_ethical-capital-investment-screening-policy","src_koontz-v-watson-opinion"],"targets":{"identity":["central_bank"],"audience":["boards","staff"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_central_bank_link-divestment-to-credit-and-liquidity-management', '{"id":"kp_central_bank_link-divestment-to-credit-and-liquidity-management","type":"key_point","title":"Link divestment to credit and liquidity management","body":"Credit downgrades and liquidity stress—like the recent Israel bond freeze—provide fiduciary cover for rebalancing into aligned issuers.","citations":["src_moody-s-downgrades-israel","src_cuyahoga-county-israel-bond-freeze"],"targets":{"identity":["central_bank"],"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'policy_screening_knowledge', '{"id":"policy_screening_knowledge","type":"policy_statement","title":"Screening intelligence","variants":[{"id":"title","body":"Screening as Cumulative Knowledge","transforms":{"tone":"title"}},{"id":"points","body":"- **Identify business model fragility.** Systematic exclusions focus attention on issuers whose operating models exhibit hidden tail risks (legal, reputational, sovereign), fragile customer bases, or supply chains exposed to human-rights litigation. (Sources: AFSC Investigate)\n- **Knowledge compounds.** Each exclusion deepens understanding of your opportunity set—linking policy pledges (apartheid-free, harm avoidance) to concrete portfolio changes and documenting the institutional lesson. (Sources: Ethical Capital: Investment Screening Policy)\n- **Integration cadence as practice standard.** Treat credible database updates as investment intelligence: aim to assess and integrate material changes within 30 days (faster when practicable) with documented rationale. (Sources: AFSC Investigate)","transforms":{"tone":"points"}}]}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'venue_notes_one_on_one', '{"id":"venue_notes_one_on_one","type":"template_snippet","title":"Venue cues","lines":["Open with shared goals; invite concerns before proposing solutions.","Offer the brief and one or two sources relevant to their role."],"targets":{"venue":["one_on_one"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'venue_notes_small_group', '{"id":"venue_notes_small_group","type":"template_snippet","title":"Venue cues","lines":["Clarify decision rights and next steps.","Use the Identity Guide to align constraints and responsibilities."],"targets":{"venue":["small_group"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'venue_notes_committee_hearing', '{"id":"venue_notes_committee_hearing","type":"template_snippet","title":"Venue cues","lines":["Keep to concrete asks; cite Policy Alignment and criteria.","Respect time limits; reference the full brief for details."],"targets":{"venue":["committee_hearing"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'venue_notes_full_board_meeting', '{"id":"venue_notes_full_board_meeting","type":"template_snippet","title":"Venue cues","lines":["Summarize the Recommended Approach and reporting cadence.","Be crisp on ‘why now’ and what changes operationally."],"targets":{"venue":["full_board_meeting"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'venue_notes_public_testimony', '{"id":"venue_notes_public_testimony","type":"template_snippet","title":"Venue cues","lines":["Time-limited: focus on 2–3 key points and one specific ask.","Bring printed one-pagers; avoid acronyms; cite one authoritative source."],"targets":{"venue":["public_testimony"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'venue_notes_written_memo', '{"id":"venue_notes_written_memo","type":"template_snippet","title":"Venue cues","lines":["Include the Model Resolution if applicable.","Attach the Policy Alignment and relevant source links."],"targets":{"venue":["written_memo"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-is-antisemitic-we-can-t-single-out-israel', '{"id":"ctr_this-is-antisemitic-we-can-t-single-out-israel","type":"counter","claim":"This is antisemitic/We can''t single out Israel.","response":"Anchor the policy in conduct: international law and human rights standards apply globally. The Jerusalem Declaration and leading Jewish organizations affirm that boycotting state conduct is protected speech, not antisemitism.","citations":["src_jerusalem-declaration-on-antisemitism","src_baldassi-v-france-echr","src_naacp-v-claiborne-hardware-458-u-s-886","src_amnesty-israel-s-system-of-apartheid"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_bds-is-illegal-in-our-state', '{"id":"ctr_bds-is-illegal-in-our-state","type":"counter","claim":"BDS is illegal in our state.","response":"State procurement laws target country boycotts. A conduct-based screen—grounded in UN Guiding Principles and applied to any violator—has survived review and mirrors how public systems like UC Regents structure exclusions.","citations":["src_un-guiding-principles-on-business-human-rights","src_koontz-v-watson-opinion","src_amawi-v-pflugerville-isd-opinion","src_jordahl-v-brnovich-opinion"],"targets":{"audience":["boards","staff","consultants","regulated","family_friends"],"level":["plain","technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_we-don-t-have-the-expertise-resources-to-evaluate-these-companies', '{"id":"ctr_we-don-t-have-the-expertise-resources-to-evaluate-these-companies","type":"counter","claim":"We don''t have the expertise/resources to evaluate these companies.","response":"Leverage third-party intelligence and manager tools: AFSC Investigate provides evidence packs, and screened indexes from MSCI/FTSE offer plug-and-play implementation with documented tracking-error budgets.","citations":["src_afsc-investigate","src_msci-esg-indexes-factsheet","src_msci-esg-focus-methodology"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-will-damage-our-relationship-with-jewish-donors-stakeholders', '{"id":"ctr_this-will-damage-our-relationship-with-jewish-donors-stakeholders","type":"counter","claim":"This will damage our relationship with Jewish donors/stakeholders.","response":"Jewish communities are not monolithic: polling shows broad support for holding the Israeli government accountable, and Jewish-led organizations publicly back conduct-based divestment grounded in human rights.","citations":["src_pew-research-jewish-americans-in-2020","src_jewish-electorate-institute-2021-poll","src_jewish-voice-for-peace-about","src_ifnotnow-mission"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_esg-has-underperformed-we-re-moving-away-from-values-investing', '{"id":"ctr_esg-has-underperformed-we-re-moving-away-from-values-investing","type":"counter","claim":"ESG has underperformed/We''re moving away from values investing.","response":"Our approach is high-active-share, research-driven investing—not an index fad. Ethical Capital''s growth strategy has delivered benchmark-like returns while holding higher cash-flow yield companies, showing exclusion discipline can coexist with market performance.","citations":["src_what-i-d-tell-your-cio-about-divestment","src_nyu-stern-esg-and-financial-performance","src_mdpi-esg-scores-and-stock-returns"],"targets":{"audience":["boards","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-creates-legal-liability-litigation-risk', '{"id":"ctr_this-creates-legal-liability-litigation-risk","type":"counter","claim":"This creates legal liability/litigation risk.","response":"Documented policy, credible sourcing, and alignment with fiduciary duties provide cover. Large funds like NBIM and PFZW publish their rationale and have avoided litigation by sticking to evidence-based conduct standards.","citations":["src_nbim-exclusion-decisions","src_pfzw-exclusion-decision","src_koontz-v-watson-opinion"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_the-board-doesn-t-have-authority-to-make-investment-decisions', '{"id":"ctr_the-board-doesn-t-have-authority-to-make-investment-decisions","type":"counter","claim":"The board doesn''t have authority to make investment decisions.","response":"Boards set policy; staff and consultants implement. UC Regents and other fiduciary boards codify exclusions in IPS language while delegating execution to investment teams under defined tracking-error limits.","citations":["src_uc-regents-investment-policy","src_amawi-v-pflugerville-isd-opinion","src_nbim-responsible-investment"],"targets":{"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-is-virtue-signaling-that-won-t-change-anything', '{"id":"ctr_this-is-virtue-signaling-that-won-t-change-anything","type":"counter","claim":"This is virtue signaling that won''t change anything.","response":"Our clients'' signals become shared policy. Every valid alert we receive is researched and added to firm-wide exclusions, and our flagship growth strategy has matched MSCI ACWI while staying values-aligned—so the work is both communal and financially credible.","citations":["src_ethical-capital-investment-screening-policy","src_what-i-d-tell-your-cio-about-divestment"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_we-need-engagement-not-divestment', '{"id":"ctr_we-need-engagement-not-divestment","type":"counter","claim":"We need engagement, not divestment.","response":"Escalation ladders work: institutions like the Church of England and Danish pensions engage first, set time-bound objectives, and divest when companies fail to remedy violations—preserving credibility and risk discipline.","citations":["src_church-of-england-pensions-board-divestment","src_akademikerpension-statement","src_nbim-exclusion-decisions"],"targets":{"audience":["boards","staff","consultants","family_friends"],"level":["plain","technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_what-about-china-saudi-arabia-other-countries', '{"id":"ctr_what-about-china-saudi-arabia-other-countries","type":"counter","claim":"What about China/Saudi Arabia/other countries?","response":"Apply one standard everywhere: our policy covers any issuer implicated in grave violations—Israeli, Chinese, American, or otherwise—using international law and third-party evidence as triggers.","citations":["src_un-guiding-principles-on-business-human-rights","src_amnesty-israel-s-system-of-apartheid","src_klp-divests-settlement-companies"],"targets":{"audience":["boards","stakeholders","consultants","family_friends"],"level":["plain","technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-politicizes-our-investment-process', '{"id":"ctr_this-politicizes-our-investment-process","type":"counter","claim":"This politicizes our investment process.","response":"Ignoring credible violations is itself a political stance. We use ethical screening as an active risk-management decision—protecting portfolios from legal, reputational, and stranded-asset exposure identified in our screening policy.","citations":["src_ethical-capital-investment-screening-policy","src_us-dol-esg-guidance","src_cuyahoga-county-israel-bond-freeze"],"targets":{"audience":["boards","staff","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_settlement-products-are-a-tiny-portion-of-these-companies-revenues', '{"id":"ctr_settlement-products-are-a-tiny-portion-of-these-companies-revenues","type":"counter","claim":"Settlement products are a tiny portion of these companies'' revenues.","response":"Even small revenue lines can signal structural risk: Caterpillar and Israeli banks face lawsuits, exclusions, and credit pressure tied to settlement activity. Screening reallocates capital to resilient issuers.","citations":["src_jury-awards-miller-vs-caterpillar","src_caterpillar-stock-reaction-to-nbim-exclusion","src_moody-s-downgrades-israel"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_how-do-we-define-settlement-involvement', '{"id":"ctr_how-do-we-define-settlement-involvement","type":"counter","claim":"How do we define ''settlement involvement''?","response":"Use objective sources: UN databases, AFSC Investigate, and vendor ESG datasets itemize settlement-linked projects. Policies trigger off this evidence and require periodic review.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_klp-divests-settlement-companies"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-requires-ongoing-monitoring-we-can-t-maintain', '{"id":"ctr_this-requires-ongoing-monitoring-we-can-t-maintain","type":"counter","claim":"This requires ongoing monitoring we can''t maintain.","response":"Set cadence and delegate: many policies mandate annual reviews plus event-driven updates fed by third-party alerts. Consultants and managers absorb the monitoring, reporting back through dashboards.","citations":["src_evergreen-state-agreement","src_msci-esg-focus-methodology","src_nbim-responsible-investment"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_our-investment-consultant-manager-won-t-implement-this', '{"id":"ctr_our-investment-consultant-manager-won-t-implement-this","type":"counter","claim":"Our investment consultant/manager won''t implement this.","response":"Ask for transparency about current constraints. Managers already deliver exclusion mandates; mainstream ESG providers offer screened benchmarks, and active oversight keeps implementation honest even when broad-market funds remain.","citations":["src_msci-esg-indexes-factsheet","src_ftse-russell-squaring-the-circle-on-carbon-track","src_church-of-england-pensions-board-divestment","src_ssrn-esg-ratings-divergence"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_msci-or-sustainability-ratings-are-more-credible-than-activist-data', '{"id":"ctr_msci-or-sustainability-ratings-are-more-credible-than-activist-data","type":"counter","claim":"MSCI or Sustainability ratings are more credible than activist data.","response":"We build proprietary screens because mainstream ratings are inconsistent and often influenced by commercial incentives. Specialist intelligence (AFSC, UN) identifies issues faster, and we overlay that insight with widely-used index tools for transparent delivery.","citations":["src_afsc-investigate","src_ssrn-esg-ratings-divergence","src_ssrn-trustworthy-esg-ratings","src_ethical-capital-investment-screening-policy"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-sets-dangerous-precedent-for-future-exclusions', '{"id":"ctr_this-sets-dangerous-precedent-for-future-exclusions","type":"counter","claim":"This sets dangerous precedent for future exclusions.","response":"Institutions already manage multiple ethical screens. UTS and SFSU frame new exclusions as extensions of long-standing SRI policies, proving conduct standards can evolve while staying disciplined.","citations":["src_union-theological-seminary-divestment","src_sfsu-foundation-policy-update"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_our-endowment-foundation-documents-prohibit-political-positions', '{"id":"ctr_our-endowment-foundation-documents-prohibit-political-positions","type":"counter","claim":"Our endowment/foundation documents prohibit political positions.","response":"Conduct-based policies focus on fiduciary risk, not partisan statements. UC Regents'' guidelines and UTS''s SRI history show boards can codify exclusions as part of their duty to manage reputational and legal risk.","citations":["src_uc-regents-investment-policy","src_union-theological-seminary-divestment"],"targets":{"audience":["boards","staff"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_we-re-a-global-institution-serving-diverse-stakeholders', '{"id":"ctr_we-re-a-global-institution-serving-diverse-stakeholders","type":"counter","claim":"We''re a global institution serving diverse stakeholders.","response":"Global fiduciaries already run conduct screens. NBIM and KLP manage worldwide portfolios while excluding companies tied to occupation abuses, demonstrating universal standards work at scale.","citations":["src_nbim-responsible-investment","src_klp-divests-settlement-companies"],"targets":{"audience":["boards","consultants"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_the-situation-is-too-complex-for-binary-decisions', '{"id":"ctr_the-situation-is-too-complex-for-binary-decisions","type":"counter","claim":"The situation is too complex for binary decisions.","response":"Policies can blend process and precision. Evergreen State College defused protests by launching a task force, while Free Money highlights that continued dialogue about ethics and finance is itself part of responsible governance.","citations":["src_evergreen-state-agreement","src_free-money-podcast-divestment-without-dissonance"],"targets":{"audience":["boards","staff","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-undermines-academic-freedom-and-exchange', '{"id":"ctr_this-undermines-academic-freedom-and-exchange","type":"counter","claim":"This undermines academic freedom and exchange.","response":"Universities exist for inquiry. Ashby Monk and Kelly Grotke highlight that divestment conversations deepen learning and institutional character; policies can target financial holdings while protecting academic collaboration.","citations":["src_free-money-podcast-divestment-without-dissonance","src_sfsu-foundation-policy-update","src_union-theological-seminary-divestment"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_we-lack-precedent-from-peer-institutions', '{"id":"ctr_we-lack-precedent-from-peer-institutions","type":"counter","claim":"We lack precedent from peer institutions.","response":"Peer action is growing: institutions from SFSU to Union Theological Seminary and pension giants like KLP have adopted universal human-rights screens. They communicate the policy as a continuity of existing SRI principles.","citations":["src_union-theological-seminary-divestment","src_sfsu-foundation-policy-update","src_klp-divests-settlement-companies"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-distracts-from-our-core-mission', '{"id":"ctr_this-distracts-from-our-core-mission","type":"counter","claim":"This distracts from our core mission.","response":"Mission and finance are intertwined. The Free Money conversation notes divestment debates deepen understanding of finance''s role, while campuses like SFSU tailored policies that supported student learning without derailing institutional objectives.","citations":["src_free-money-podcast-divestment-without-dissonance","src_sfsu-foundation-policy-update"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_divestment-will-significantly-hurt-performance-and-beneficiaries', '{"id":"ctr_divestment-will-significantly-hurt-performance-and-beneficiaries","type":"counter","claim":"Divestment will significantly hurt performance and beneficiaries.","response":"Naive exclusions can be costly, but professional managers use optimization and tracking-error limits to preserve factor exposures and benchmark-like returns.","citations":["src_msci-esg-index-construction"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_exclusions-violate-fiduciary-duty', '{"id":"ctr_exclusions-violate-fiduciary-duty","type":"counter","claim":"Exclusions violate fiduciary duty.","response":"Fiduciary duty requires prudence, loyalty, and attention to material risk and client objectives. Values alignment and risk management can be implemented within a disciplined, return-seeking mandate.","citations":["src_un-pri-fiduciary-duty","src_us-dol-esg-guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_divestment-is-ineffective-someone-else-just-buys-the-shares', '{"id":"ctr_divestment-is-ineffective-someone-else-just-buys-the-shares","type":"counter","claim":"Divestment is ineffective; someone else just buys the shares.","response":"The primary effects are reputational and in primary capital markets. Policies influence lenders, underwriters, and index providers and shift the cost of capital for targeted issuers.","citations":["src_oxford-ssee-research"],"targets":{"audience":["boards","staff","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_exclusions-reduce-diversification-too-much', '{"id":"ctr_exclusions-reduce-diversification-too-much","type":"counter","claim":"Exclusions reduce diversification too much.","response":"Well-designed policies are narrow and conduct-based. Managers maintain diversification across sectors and factors while removing a limited set of issuers that breach defined standards.","citations":["src_msci-portfolio-construction"],"targets":{"audience":["boards","staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_our-index-funds-can-t-accommodate-exclusions', '{"id":"ctr_our-index-funds-can-t-accommodate-exclusions","type":"counter","claim":"Our index funds can''t accommodate exclusions.","response":"Major providers offer screened versions of core indexes, and mandates can overlay exclusion lists via optimization. Public pensions already run Israel/settlement screens inside benchmark-aware vehicles.","citations":["src_ftse-russell-squaring-the-circle-on-carbon-track","src_msci-esg-indexes-factsheet","src_church-of-england-pensions-board-divestment"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_israeli-companies-provide-essential-technology-services', '{"id":"ctr_israeli-companies-provide-essential-technology-services","type":"counter","claim":"Israeli companies provide essential technology/services.","response":"A conduct-first policy distinguishes between legitimate business and grave rights violations. Exclusions target companies whose revenue or technology is tied to occupation abuses; suppliers meeting standards remain investable.","citations":["src_klp-divests-settlement-companies","src_akademikerpension-statement","src_afsc-investigate"],"targets":{"audience":["boards","staff","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_un-databases-are-politically-motivated-and-unreliable', '{"id":"ctr_un-databases-are-politically-motivated-and-unreliable","type":"counter","claim":"UN databases are politically motivated and unreliable.","response":"Treat activist research as the specialist edge. Ground-level groups like AFSC or the UN database track evidence sooner than generalized ESG vendors; large funds (e.g., KLP) use them as the investigative spine and apply mainstream index tools for implementation.","citations":["src_afsc-investigate","src_un-database-on-settlements","src_klp-divests-settlement-companies","src_ssrn-impact-of-esg-rating-changes"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_apartheid-divestment-was-chaos-why-repeat-it', '{"id":"ctr_apartheid-divestment-was-chaos-why-repeat-it","type":"counter","claim":"Apartheid divestment was chaos; why repeat it?","response":"Apartheid divestment proved you can stand on ethics without breaking returns. Our firm’s policy embraces that precedent: we run apartheid-free portfolios with documented process and rely on sustained, multi-channel pressure—divestment plus engagement—just as history shows.","citations":["src_what-i-d-tell-your-cio-about-divestment","src_ethical-capital-investment-screening-policy"],"targets":{"audience":["boards","stakeholders","family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_how-do-you-know-these-activist-lists-aren-t-biased', '{"id":"ctr_how-do-you-know-these-activist-lists-aren-t-biased","type":"counter","claim":"How do you know these activist lists aren''t biased?","response":"We do not outsource ethics to third-party lists. About 40% of our exclusions are unique because we rebuild evidence in-house and document every call; activist data is an input, not the final answer.","citations":["src_ethical-capital-investment-screening-policy","src_ssrn-esg-ratings-divergence"],"targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'ctr_this-sounds-expensive-or-complicated-where-do-i-even-start', '{"id":"ctr_this-sounds-expensive-or-complicated-where-do-i-even-start","type":"counter","claim":"This sounds expensive or complicated—where do I even start?","response":"Ethical screening is messy in practice—that’s why investors hire professionals to map the evidence, translate it into exclusions, and keep the process on track.","citations":["src_ethical-capital-investment-screening-policy"],"targets":{"audience":["staff","consultants","family_friends"],"level":["plain","technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'tmpl_model_resolution', '{"id":"tmpl_model_resolution","type":"template_snippet","title":"Model Resolution","markdown":"RESOLVED: That the {localName} shall adopt a conduct-based investment exclusion policy, instructing its investment managers to avoid purchasing or holding securities of companies for which there is credible, public evidence of severe human rights violations, including complicity in violations under international humanitarian law; and be it further\n\nRESOLVED: That the {localName} directs its investment managers to implement this policy using professional portfolio construction techniques that maintain diversification and risk controls, including explicit tracking error limits relative to the benchmark; and be it further\n\nRESOLVED: That the {localName} will publish an exclusion rationale and a public list of excluded issuers, reviewed at least annually; and be it further\n\nRESOLVED: That the {localName} will phase implementation over an orderly period and report progress and holdings, subject to prudent trading and confidentiality constraints.","targets":{"audience":["fiduciary","regulated"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'tmpl_government_policy', '{"id":"tmpl_government_policy","type":"template_snippet","title":"Government Policy Template","markdown":"Approved Issuers Policy (template)\\n\\nPurpose: Maintain an Approved Issuers List for fixed-income investing that aligns treasury practices with ethical standards and risk controls while complying with law and procurement rules.\\n\\nCriteria: Exclude issuers with credible, public evidence of severe human rights violations (including complicity in violations under international humanitarian law). Consider authoritative sources (sanctions lists, court decisions, official investigations) and reputable human-rights reporting.\\n\\nPortfolio Constraints: Preserve target duration, credit quality, liquidity, and diversification consistent with the investment policy and statutes.\\n\\nProcess: Treasurer (or designee) maintains Approved and Prohibited Issuer lists; legal/procurement review; annual review and event-driven updates; exceptions require written rationale, legal review, time limits, and notification to the governing body.\\n\\nTransparency & Reporting: Publish criteria and current lists subject to prudent trading and confidentiality; report changes and rationales at least annually.\\n\\nCompliance: This policy operates subject to state law, investment statutes, and procurement rules; in case of conflict, legal requirements prevail.","targets":{"identity":["government"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'note_cio', '{"id":"note_cio","type":"template_snippet","title":"CIO Implementation Note","markdown":"Implementation focuses on preserving benchmark alignment: specify a tracking-error budget, maintain factor neutrality, use vendor screened indexes or optimization constraints, and phase trades to avoid divestment shocks.","targets":{"audience":["fiduciary"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_request-current-holdings-exposure-report-from-ma', '{"id":"step_request-current-holdings-exposure-report-from-ma","type":"next_step","text":"Request current holdings/exposure report from managers.","targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_adopt-written-conduct-criteria-and-escalation-pr', '{"id":"step_adopt-written-conduct-criteria-and-escalation-pr","type":"next_step","text":"Adopt written conduct criteria and escalation process.","targets":{"audience":["boards"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_mandate-tracking-error-and-factor-exposure-contr', '{"id":"step_mandate-tracking-error-and-factor-exposure-contr","type":"next_step","text":"Mandate tracking error and factor exposure controls for managers.","targets":{"audience":["staff","consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_publish-a-public-exclusions-list-with-brief-rati', '{"id":"step_publish-a-public-exclusions-list-with-brief-rati","type":"next_step","text":"Publish a public exclusions list with brief rationales.","targets":{"audience":["boards","staff"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_schedule-an-annual-review-and-public-update', '{"id":"step_schedule-an-annual-review-and-public-update","type":"next_step","text":"Schedule an annual review and public update.","targets":{"audience":["boards","staff"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_individual_list-your-current-retirement-and-brokerage-h', '{"id":"step_individual_list-your-current-retirement-and-brokerage-h","type":"next_step","text":"List your current retirement and brokerage holdings and note what you want to avoid.","targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_individual_shift-savings-into-screened-etfs-or-managed-', '{"id":"step_individual_shift-savings-into-screened-etfs-or-managed-","type":"next_step","text":"Shift savings into screened ETFs or managed accounts that match your risk tolerance.","targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_individual_set-a-reminder-to-review-allocations-annuall', '{"id":"step_individual_set-a-reminder-to-review-allocations-annuall","type":"next_step","text":"Set a reminder to review allocations annually or after major life changes.","targets":{"identity":["individual"],"audience":["individuals"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_family_friends_share-your-own-experience-reviewing-accounts', '{"id":"step_family_friends_share-your-own-experience-reviewing-accounts","type":"next_step","text":"Share your own experience reviewing accounts and choosing screened funds.","targets":{"identity":["individual"],"audience":["family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_family_friends_offer-to-walk-through-their-brokerage-or-ret', '{"id":"step_family_friends_offer-to-walk-through-their-brokerage-or-ret","type":"next_step","text":"Offer to walk through their brokerage or retirement platform to locate values-aligned options.","targets":{"identity":["individual"],"audience":["family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'step_family_friends_encourage-them-to-revisit-the-conversation-a', '{"id":"step_family_friends_encourage-them-to-revisit-the-conversation-a","type":"next_step","text":"Encourage them to revisit the conversation after they’ve tried a small allocation shift.","targets":{"identity":["individual"],"audience":["family_friends"],"level":["plain"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'policy_alignment', '{"id":"policy_alignment","type":"policy_statement","title":"Policy alignment criteria","bullets":["Documented, explainable criteria with public rationales (no black boxes)","Conduct-first screening for risk mitigation; product screens for categorical harms when aligned with mission","Evidence-based decisions using primary and reputable sources with citations","Regular review cadence (at least annually) and event-driven updates","Escalation path from engagement to exclusion; documented exceptions with time limits","Implementation discipline: tracking-error guardrails, diversification, factor neutrality, phased trading","Transparency: publish criteria and lists subject to prudent trading and confidentiality"],"citations":["src_ethical-capital-screening-policy"]}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_pension_staff', '{"id":"opener_pension_staff","type":"opener","text":"Staff can frame this as fiduciary risk governance: pull exposure runs from managers, cite peer pensions already using conduct screens, and show how tracking-error guardrails keep liabilities protected.","targets":{"identity":["public_pension","corporate_pension"],"audience":["staff"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_pension_consultant', '{"id":"opener_pension_consultant","type":"opener","text":"Consultants can bring trustees a vote-ready memo: define conduct triggers, outline optimized benchmark overlays, and hand over templates that document prudence while staff executes.","targets":{"identity":["public_pension","corporate_pension"],"audience":["consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_mission_staff', '{"id":"opener_mission_staff","type":"opener","text":"Mission-driven investment staff can translate campus and program demands into policy: map donor restrictions, propose phased exclusions, and pair mission statements with investment risk notes.","targets":{"identity":["endowment","foundation"],"audience":["staff"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_mission_consultant', '{"id":"opener_mission_consultant","type":"opener","text":"Advisors can frame alignment as reputational risk management: benchmark peer endowments, package screened fund menus, and show spending policy resilience under the exclusion set.","targets":{"identity":["endowment","foundation"],"audience":["consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_government_staff', '{"id":"opener_government_staff","type":"opener","text":"Treasury staff can lead with compliance: align exclusions with procurement statutes, present approved issuer watchlists, and document that liquidity and cash-flow schedules stay intact.","targets":{"identity":["government"],"audience":["staff"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_government_consultant', '{"id":"opener_government_consultant","type":"opener","text":"Public-fund advisors can translate advocacy into policy language: cite municipal precedents, supply approved issuer models, and brief council members on sunshine-law friendly reporting cadences.","targets":{"identity":["government"],"audience":["consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_sovereign_staff', '{"id":"opener_sovereign_staff","type":"opener","text":"Sovereign reserve teams can ground the ask in mandate protection: reference Santiago Principles, show best-efforts exclusions preserve liquidity, and schedule periodic policy reviews.","targets":{"identity":["central_bank","swf"],"audience":["staff"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_sovereign_consultant', '{"id":"opener_sovereign_consultant","type":"opener","text":"External partners can add independent credibility: document human-rights triggers, align with IMF governance guidance, and demonstrate tracking-error caps for reserve portfolios.","targets":{"identity":["central_bank","swf"],"audience":["consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_insurance_staff', '{"id":"opener_insurance_staff","type":"opener","text":"Insurance investment teams should tie exclusions to liability management: highlight RBC-friendly screened options, coordinate with actuaries on duration, and show rating agency expectations stay satisfied.","targets":{"identity":["insurance"],"audience":["staff"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'opener_insurance_consultant', '{"id":"opener_insurance_consultant","type":"opener","text":"Consultants can bring carriers a capital-friendly roadmap: propose conduct screens that respect ALM tests, provide optimization outputs, and script regulator-ready documentation.","targets":{"identity":["insurance"],"audience":["consultants"],"level":["technical"]}}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_strategy_regulatory_drivers', '{"id":"kp_strategy_regulatory_drivers","type":"key_point","title":"Regulatory drivers: codify compliance-first divestment","body":"Strategy spectrum: conduct-based exclusion lists, optimized overlays on core benchmarks, or segregated compliance sleeves to satisfy fiduciary and statutory directives.\nImplementation notes: lean on attorney general guidance, procurement statutes, and consultant attestations; pair exclusions with tracking-error budgets and factor neutrality tests.\nOutcomes to monitor: variance vs policy benchmark (<50 bps TE targets), documented liability risk reduction, regulator-ready audit trails, and stakeholder briefings that underline prudence.","citations":["src_un-guiding-principles-on-business-human-rights","src_koontz-v-watson-opinion","src_amawi-v-pflugerville-isd-opinion"],"assertions":["assert_tracking_error_controls","assert_modern_fiduciary_guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"identity":["public_pension","corporate_pension","government","central_bank","swf","insurance"],"motivation":["regulatory_drivers"]},"tags":["strategy_spectrum","motivation_regulatory"]}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_strategy_internal_leadership', '{"id":"kp_strategy_internal_leadership","type":"key_point","title":"Internal leadership: align mission and capital without losing discipline","body":"Strategy spectrum: mission-screen overlays, carve-out impact sleeves, delegated investment committee mandates, and donor-aligned policy statements with phased implementation schedules.\nImplementation notes: connect divestment criteria to charter language, donor restrictions, and campus/program goals; pilot with separate accounts or manager guidelines before system-wide rollout.\nOutcomes to monitor: mission KPI dashboards, spending policy resilience, peer benchmarking, and qualitative feedback loops from leadership and program staff.","citations":["src_ethical-capital-screening-policy","src_nacubo_tiaa_spending"],"assertions":["assert_endowment_spending_norms"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"identity":["endowment","foundation","individual"],"motivation":["internal_leadership"]},"tags":["strategy_spectrum","motivation_internal"]}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_strategy_external_stakeholders', '{"id":"kp_strategy_external_stakeholders","type":"key_point","title":"External stakeholders: convert campaign pressure into structured capital moves","body":"Strategy spectrum: responsive divest-reinvest mandates, coalition-built exclusion lists, community reinvestment pools, and transparency dashboards that track follow-through.\nImplementation notes: co-design criteria with organizers, publish escalation ladders, and coordinate communications with governance, donor, and beneficiary groups.\nOutcomes to monitor: stakeholder sentiment shifts, media/regulator responses, capital redeployment volumes, and engagement opportunities created by divestment decisions.","citations":["src_afsc-investigate","src_naacp-v-claiborne-hardware-458-u-s-886"],"assertions":["assert_public_pension_transparency_governance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"identity":["public_pension","government","endowment","foundation","individual"],"motivation":["external_stakeholders"]},"tags":["strategy_spectrum","motivation_external"]}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_outcome_identity_alignment', '{"id":"kp_outcome_identity_alignment","type":"key_point","title":"Outcome: investments align with identity","body":"Clear divestment criteria anchor portfolios in the institution’s stated ethics, letting trustees and staff demonstrate that capital flows reflect mission and fiduciary commitments. Alignment messaging reassures beneficiaries, donors, and community partners that investment actions match identity.","citations":["src_ethical-capital-screening-policy"],"assertions":["assert_modern_fiduciary_guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"],"identity":["public_pension","corporate_pension","government","central_bank","swf","insurance","endowment","foundation"]},"tags":["outcome","strategy_spectrum"]}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_outcome_regulatory_confidence', '{"id":"kp_outcome_regulatory_confidence","type":"key_point","title":"Outcome: documented compliance confidence","body":"Conduct-based exclusions, optimization reports, and counsel sign-offs create an audit trail that satisfies fiduciary and statutory obligations. Regulators and attorneys general see proactive risk management, lowering litigation exposure and procurement challenges.","citations":["src_un-guiding-principles-on-business-human-rights","src_koontz-v-watson-opinion"],"assertions":["assert_modern_fiduciary_guidance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain","technical"],"identity":["public_pension","corporate_pension","government","central_bank","swf","insurance"],"motivation":["regulatory_drivers"]},"tags":["outcome","motivation_regulatory"]}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_outcome_mission_trust', '{"id":"kp_outcome_mission_trust","type":"key_point","title":"Outcome: mission trust reinforced","body":"When boards drive divestment, they can show donors, students, and program staff that capital decisions back institutional values without jeopardising spending policies. Transparent trade plans and impact dashboards keep leadership, faculty, and community stakeholders aligned.","citations":["src_ethical-capital-screening-policy"],"assertions":["assert_endowment_spending_norms"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"],"identity":["endowment","foundation"],"motivation":["internal_leadership"]},"tags":["outcome","motivation_internal"]}');
INSERT INTO nodes (version, id, payload) VALUES ('2025-09-27', 'kp_outcome_stakeholder_momentum', '{"id":"kp_outcome_stakeholder_momentum","type":"key_point","title":"Outcome: stakeholder momentum sustained","body":"Coalitions see tangible follow-through when portfolios exit targeted issuers and capital is redeployed toward community priorities. Publishing progress dashboards and reinvestment pipelines keeps organizers engaged and channels pressure into ongoing governance improvements.","citations":["src_afsc-investigate"],"assertions":["assert_public_pension_transparency_governance"],"targets":{"audience":["boards","staff","consultants"],"level":["plain"],"identity":["public_pension","government","endowment","foundation"],"motivation":["external_stakeholders"]},"tags":["outcome","motivation_external"]}');
