INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_benchmark_grade_exclusions', '{"id":"assert_benchmark_grade_exclusions","title":"Benchmark-grade exclusions maintain performance","statement":"Large public asset owners such as NBIM maintain benchmark-like performance after conduct-based exclusions by rebalancing and controlling factor exposures.","evidence":["src_nbim-responsible-investment","src_nbim-exclusion-of-companies"],"supports":["kp_professional-exclusions-maintain-benchmark-like-performance"],"confidence":"high","tags":["benchmarking","exclusions"],"author":"ethical-capital","lastReviewed":"2025-09-27","reviewer":"dryvest-editorial"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_tracking_error_controls', '{"id":"assert_tracking_error_controls","title":"Tracking error can be budgeted and controlled","statement":"Index providers (MSCI, FTSE Russell) document exclusion methodologies that explicitly budget tracking error and maintain target factor exposures.","evidence":["src_msci-esg-indexes-methodology","src_ftse-russell-squaring-the-circle-on-carbon-track"],"supports":["kp_tracking-error-and-factor-neutrality"],"confidence":"medium","tags":["portfolio-engineering","tracking-error"],"author":"ethical-capital","lastReviewed":"2025-09-27","reviewer":"dryvest-editorial"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_modern_fiduciary_guidance', '{"id":"assert_modern_fiduciary_guidance","title":"Modern fiduciary guidance recognizes ESG materiality","statement":"UN PRI and US DOL guidance clarify that material ESG factors and client objectives can be incorporated without abandoning prudent, return-seeking management.","evidence":["src_un-pri-fiduciary-duty-in-the-21st-century","src_us-dol-esg-factors-fact-sheet"],"supports":["kp_fiduciary-duty-and-values-alignment"],"confidence":"medium","tags":["fiduciary","policy"],"author":"ethical-capital","lastReviewed":"2025-09-27","reviewer":"dryvest-editorial"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_corporate_pension_erisa_duty', '{"id":"assert_corporate_pension_erisa_duty","title":"Corporate pensions operate under ERISA fiduciary standards","statement":"Corporate defined-benefit plans are governed by ERISA, requiring fiduciaries to act solely in participants'' interests and document prudent investment processes.","evidence":["src_us-dol-erisa-overview","src_pbgc_investment_policy"],"supports":[],"tags":["corporate_pension","fiduciary"],"confidence":"high","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_public_pension_transparency_governance', '{"id":"assert_public_pension_transparency_governance","title":"Public pensions balance fiduciary duty with public accountability","statement":"State and local retirement systems are governed by fiduciary boards that operate under open-meeting, disclosure, and beneficiary-focused standards.","evidence":["src_nasra_public_pension_basics","src_gfoa_pension_best_practices"],"supports":[],"tags":["public_pension","governance"],"confidence":"medium","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_endowment_spending_norms', '{"id":"assert_endowment_spending_norms","title":"Endowments target 4-5% spending to preserve purchasing power","statement":"University and foundation endowments typically adopt spending policies near 4-5% to balance mission support with intergenerational equity.","evidence":["src_nacubo_tiaa_spending","src_irs_foundation_distribution"],"supports":[],"tags":["endowment","spending"],"confidence":"medium","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_foundation_distribution_requirement', '{"id":"assert_foundation_distribution_requirement","title":"Private foundations must distribute at least 5% annually","statement":"U.S. private foundations are required to distribute a minimum of 5% of average asset value each year toward charitable purposes.","evidence":["src_irs_foundation_distribution"],"supports":[],"tags":["foundation","distribution"],"confidence":"high","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_insurance_liability_alignment', '{"id":"assert_insurance_liability_alignment","title":"Insurers invest with liability-matching and capital rules","statement":"Insurance portfolios are managed to match expected policy liabilities and satisfy NAIC risk-based capital requirements, limiting allocation flexibility.","evidence":["src_naic_risk_based_capital","src_naic_asset_liability_management"],"supports":[],"tags":["insurance","liability_matching"],"confidence":"medium","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_central_bank_independence', '{"id":"assert_central_bank_independence","title":"Central banks defend policy independence and balance sheet integrity","statement":"Modern central banks manage reserves with mandates that emphasize monetary policy independence, balance sheet risk controls, and political neutrality.","evidence":["src_bis_central_bank_independence","src_imf_central_bank_mandates"],"supports":[],"tags":["central_bank","governance"],"confidence":"medium","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_swf_mandates_and_santiago_principles', '{"id":"assert_swf_mandates_and_santiago_principles","title":"Sovereign wealth funds follow mandate-specific investment frameworks","statement":"Sovereign wealth funds operate under stabilization, savings, or development mandates with governance expectations codified by the Santiago Principles.","evidence":["src_swf_institute_overview","src_imf_santiago_principles"],"supports":[],"tags":["swf","governance"],"confidence":"medium","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_government_investment_policy_principles', '{"id":"assert_government_investment_policy_principles","title":"Municipal investment policies prioritize safety, liquidity, and transparency","statement":"Government treasuries and cash pools follow GFOA best practices emphasizing safety, liquidity, diversification, and documented exceptions for public accountability.","evidence":["src_gfoa_investment_policy"],"supports":[],"tags":["government","investment_policy"],"confidence":"medium","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO assertions (version, id, payload) VALUES ('2025-09-27', 'assert_individual_retirement_guidance', '{"id":"assert_individual_retirement_guidance","title":"Household investors balance risk capacity, liquidity, and values","statement":"Individual investors are encouraged to keep emergency reserves, align retirement accounts with stated goals, and use screened products where available without exceeding risk tolerance.","evidence":["src_cfpb_retirement_accounts"],"supports":[],"tags":["individual","retirement"],"confidence":"medium","author":"ethical-capital","reviewer":"dryvest-editorial","lastReviewed":"2025-09-27"}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'individual', '{"id":"individual","label":"Individual Investor","shortDescription":"Households balancing risk capacity, liquidity needs, and values across taxable and retirement accounts.","timeHorizon":"Varies by life stage (emergency reserves, retirement horizon, legacy goals)","typicalWithdrawal":"Income replacement withdrawals begin after retirement; otherwise focused on accumulation.","governanceStyle":"Self-directed choices informed by advisors, custodians, or accountability groups.","keyConstraints":["Emergency liquidity and cash buffers","Risk tolerance and capacity","Product availability via custodians","Tax considerations"],"stakeholders":["Account owner","Household/partner","Future heirs or philanthropic goals"],"assertions":["assert_individual_retirement_guidance"],"sources":["src_cfpb_retirement_accounts"],"tags":["individual","retirement"]}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'corporate_pension', '{"id":"corporate_pension","label":"Corporate Pension Plan","shortDescription":"ERISA-regulated defined-benefit plans stewarded by trustees for participant benefit.","timeHorizon":"Long-dated liability profile (20-30 years) with de-risking glidepaths","typicalWithdrawal":"Benefit obligations determined actuarially; payouts tied to participant retirements.","governanceStyle":"Named fiduciaries and investment committees operating under ERISA documentation and prudence rules.","keyConstraints":["ERISA fiduciary duty and exclusive-benefit rule","Funding status and PBGC oversight","Corporate sponsor risk management","Accounting impact (ASC 715)"],"stakeholders":["Active employees and retirees","Plan trustees","Corporate sponsor and treasury","Unions or employee representatives"],"assertions":["assert_corporate_pension_erisa_duty"],"sources":["src_us-dol-erisa-overview","src_pbgc_investment_policy"],"tags":["pension","erisa"]}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'public_pension', '{"id":"public_pension","label":"Public Pension System","shortDescription":"State and local retirement systems balancing fiduciary duty with statutory and public accountability.","timeHorizon":"Intergenerational horizon with actuarial smoothing across economic cycles","typicalWithdrawal":"Benefit cash flows linked to retiree demographics and statutory formulas.","governanceStyle":"Boards comprised of trustees, beneficiaries, and public appointees under open-meeting and disclosure statutes.","keyConstraints":["Statutory fiduciary obligations","Political and public oversight","Funding ratio targets","Collective bargaining and legislative constraints"],"stakeholders":["Active members and retirees","Taxpayers","Legislators and oversight bodies","Public watchdog organizations"],"assertions":["assert_public_pension_transparency_governance"],"sources":["src_nasra_public_pension_basics","src_gfoa_pension_best_practices"],"tags":["public_pension","governance"]}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'endowment', '{"id":"endowment","label":"Endowment","shortDescription":"Perpetual investment pools supporting academic or mission-driven institutions.","timeHorizon":"Perpetual capital with spending rules balancing current and future beneficiaries","typicalWithdrawal":"4-5% policy spending rate averaged over market cycles","governanceStyle":"Board of trustees or investment committee aligns mission with fiduciary prudence and donor intent.","keyConstraints":["Mission alignment and donor restrictions","Intergenerational equity","Liquidity for capital commitments and spending","Peer benchmarking"],"stakeholders":["Students and faculty","Donors and alumni","Community served","Institutional leadership"],"assertions":["assert_endowment_spending_norms"],"sources":["src_nacubo_tiaa_spending"],"tags":["endowment","mission"]}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'foundation', '{"id":"foundation","label":"Private Foundation","shortDescription":"Grantmaking entities required to distribute funds annually while preserving mission alignment.","timeHorizon":"Perpetual or time-limited based on charter","typicalWithdrawal":"≥5% IRS minimum distribution requirement","governanceStyle":"Board/trustees with philanthropic mandate and IRS compliance responsibilities.","keyConstraints":["IRS distribution and reporting rules","Mission program alignment","Donor intent","Reputational stewardship"],"stakeholders":["Grantees and beneficiary communities","Donors/founders","Program staff","Regulators"],"assertions":["assert_foundation_distribution_requirement"],"sources":["src_irs_foundation_distribution"],"tags":["foundation","distribution"]}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'insurance', '{"id":"insurance","label":"Insurance Company Portfolio","shortDescription":"Balance sheets managed to meet policyholder liabilities under risk-based capital rules.","timeHorizon":"Matched to liability cash flows, typically medium-term with precise duration targets","typicalWithdrawal":"Liability-driven payouts; investment income supports statutory reserves.","governanceStyle":"Investment committees with actuarial, risk, and regulatory oversight.","keyConstraints":["Risk-based capital requirements","Asset-liability duration matching","Policyholder guarantees","Rating agency expectations"],"stakeholders":["Policyholders","Shareholders or mutual owners","Regulators","Rating agencies"],"assertions":["assert_insurance_liability_alignment"],"sources":["src_naic_risk_based_capital","src_naic_asset_liability_management"],"tags":["insurance","liability_matching"]}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'central_bank', '{"id":"central_bank","label":"Central Bank Reserve Portfolio","shortDescription":"Official-sector investors balancing monetary policy mandates with reserve safety and liquidity.","timeHorizon":"Mandate-driven; reserves managed for stability rather than return maximization","typicalWithdrawal":"Interventions and liquidity operations rather than fixed spending","governanceStyle":"Independent policy boards supported by reserve management teams and statutory mandates.","keyConstraints":["Monetary policy independence","Capital preservation and liquidity","Political neutrality","International credibility"],"stakeholders":["Citizens","Government","Financial institutions","Global partners"],"assertions":["assert_central_bank_independence"],"sources":["src_bis_central_bank_independence","src_imf_central_bank_mandates"],"tags":["central_bank","policy"]}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'government', '{"id":"government","label":"Municipal Investment Pool","shortDescription":"City, county, or state treasuries investing public funds under safety, liquidity, and transparency mandates.","timeHorizon":"Short- to medium-term cash flows aligned to budget cycles","typicalWithdrawal":"Liquidity needs for operations, debt service, and capital projects.","governanceStyle":"Treasurer and investment committee operating under adopted investment policy statements and public reporting.","keyConstraints":["State statute and charter requirements","Safety-liquidity-yield hierarchy","Public transparency","Counterparty and concentration limits"],"stakeholders":["Residents","Elected officials","Public service departments","Rating agencies"],"assertions":["assert_government_investment_policy_principles"],"sources":["src_gfoa_investment_policy"],"tags":["government","investment_policy"]}');
INSERT INTO entities (version, id, payload) VALUES ('2025-09-27', 'swf', '{"id":"swf","label":"Sovereign Wealth Fund","shortDescription":"State-owned investment funds pursuing stabilization, savings, or development mandates.","timeHorizon":"Depends on mandate: near-term stabilization, intergenerational saving, or long-term development","typicalWithdrawal":"Mandate-driven distributions for budget support or development projects.","governanceStyle":"Government sponsor with professional management aligned to Santiago Principles.","keyConstraints":["Mandate clarity and legal framework","Political oversight","Public reporting and Santiago Principles","Currency and commodity exposure management"],"stakeholders":["Current citizens","Future generations","Government ministries","International partners"],"assertions":["assert_swf_mandates_and_santiago_principles"],"sources":["src_swf_institute_overview","src_imf_santiago_principles"],"tags":["swf","sovereign"]}');
